import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import { motion, AnimatePresence } from 'framer-motion';
import Tooltip from './Tooltip';

const Navbar = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const { theme, toggleTheme } = useTheme();
  const { language, changeLanguage, texts = {} } = useLanguage() || {};
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLangMenuOpen, setIsLangMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();
  
  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrolled]);

  // Close menus when route changes
  useEffect(() => {
    setIsMenuOpen(false);
    setIsLangMenuOpen(false);
  }, [location.pathname]);
  
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
    if (isLangMenuOpen) setIsLangMenuOpen(false);
  };

  const toggleLangMenu = () => {
    setIsLangMenuOpen(!isLangMenuOpen);
    if (isMenuOpen) setIsMenuOpen(false);
  };

  // Animation variants
  const navbarVariants = {
    initial: {
      backgroundColor: theme === 'dark' ? 'rgba(17, 24, 39, 0)' : 'rgba(255, 255, 255, 0)',
      boxShadow: '0 0 0 rgba(0, 0, 0, 0)',
      height: 70
    },
    scrolled: {
      backgroundColor: theme === 'dark' ? 'rgba(17, 24, 39, 0.95)' : 'rgba(255, 255, 255, 0.95)',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
      height: 60,
      borderBottom: theme === 'dark' ? '1px solid rgba(55, 65, 81, 0.5)' : '1px solid rgba(229, 231, 235, 0.5)'
    }
  };

  const logoVariants = {
    initial: { opacity: 0, x: -20 },
    animate: { opacity: 1, x: 0, transition: { duration: 0.5 } },
    hover: { scale: 1.05, transition: { duration: 0.2 } }
  };

  const menuItemVariants = {
    closed: { opacity: 0, y: -10 },
    open: (i) => ({ 
      opacity: 1, 
      y: 0, 
      transition: { delay: i * 0.1, duration: 0.3 } 
    }),
    hover: { 
      scale: 1.05, 
      transition: { duration: 0.2 },
      textShadow: "0 0 8px rgba(255,255,255,0.5)"
    }
  };

  const mobileMenuVariants = {
    closed: { 
      opacity: 0, 
      height: 0,
      transition: {
        duration: 0.3,
        when: "afterChildren",
        staggerChildren: 0.05,
        staggerDirection: -1
      }
    },
    open: { 
      opacity: 1, 
      height: 'auto',
      transition: {
        duration: 0.3,
        when: "beforeChildren",
        staggerChildren: 0.05,
        staggerDirection: 1,
        delayChildren: 0.1
      }
    }
  };

  const langButtonVariants = {
    hover: { scale: 1.1 },
    tap: { scale: 0.95 }
  };

  // Language options
  const languageOptions = [
    { code: 'en', name: 'English', flag: '🇺🇸' },
    { code: 'ru', name: 'Русский', flag: '🇷🇺' },
    { code: 'kz', name: 'Қазақша', flag: '🇰🇿' }
  ];

  // Default menu item names in case translations aren't loaded yet
  const defaultMenuItems = {
    home: 'Home',
    contact: 'Contact',
    checkNews: 'Check News',
    history: 'History',
    profile: 'Profile',
    admin: 'Admin',
    login: 'Login',
    register: 'Register',
    logout: 'Logout'
  };

  const menuItems = [
    { name: texts.home || defaultMenuItems.home, path: '/', showAlways: true },
    { name: texts.contact || defaultMenuItems.contact, path: '/contact', showAlways: true },
    { name: texts.checkNews || defaultMenuItems.checkNews, path: '/check-news', showLoggedIn: true },
    { name: texts.history || defaultMenuItems.history, path: '/history', showLoggedIn: true },
    { name: texts.profile || defaultMenuItems.profile, path: '/profile', showLoggedIn: true },
    { name: texts.admin || defaultMenuItems.admin, path: '/admin', showAdmin: true }
  ];

  // Filter menu items based on auth state
  const filteredMenuItems = menuItems.filter(item => 
    (item.showAlways) || 
    (currentUser && item.showLoggedIn) || 
    (currentUser?.role === 'admin' && item.showAdmin)
  );

  // Active and inactive classes for menu items
  const activeClass = `${theme === 'dark' 
    ? 'bg-gradient-to-r from-blue-900 to-indigo-900 text-white border-transparent' 
    : 'bg-gradient-to-r from-blue-100 to-blue-50 text-blue-700 border-blue-200'}`;
  
  const inactiveClass = `${theme === 'dark'
    ? 'text-gray-300 hover:text-white hover:bg-gray-800 border-transparent'
    : 'text-gray-700 hover:text-blue-700 hover:bg-blue-50 border-transparent'}`;

  return (
    <motion.nav 
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
        theme === 'dark' ? 'dark:bg-gray-900' : 'bg-white dark:bg-gray-900'
      }`}
      initial="initial"
      animate={scrolled ? "scrolled" : "initial"}
      variants={navbarVariants}
    >
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-full">
          <motion.div
            variants={logoVariants}
            initial="initial"
            animate="animate"
            whileHover="hover"
          >
            <Link to="/" className="flex items-center gap-2">
              <div className={`w-8 h-8 rounded-lg flex items-center justify-center overflow-hidden 
                ${theme === 'dark' 
                  ? 'bg-gradient-to-br from-blue-600 to-indigo-700' 
                  : 'bg-gradient-to-br from-blue-500 to-indigo-600'}`}
              >
                <motion.svg 
                  className="w-5 h-5 text-white" 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24" 
                  xmlns="http://www.w3.org/2000/svg"
                  initial={{ rotate: 0 }}
                  animate={{ rotate: 360 }}
                  transition={{ duration: 3, repeat: Infinity, ease: "linear" }}
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" 
                  />
                </motion.svg>
              </div>
              <span className={`font-bold ${scrolled ? 'text-lg' : 'text-xl'} transition-all duration-300 
                ${theme === 'dark' ? 'text-white' : 'text-gray-800 dark:text-white'}`}
              >
                Fake News Detector
              </span>
            </Link>
          </motion.div>
          
          {/* Mobile menu buttons */}
          <div className="flex items-center md:hidden gap-2">
            {/* Language selector for mobile */}
            <div className="relative">
              <Tooltip text={texts?.tooltipLang || 'Change language'}>
                <motion.button
                  onClick={toggleLangMenu}
                  className={`p-2 rounded-lg flex items-center justify-center 
                    ${theme === 'dark' 
                      ? 'bg-gray-800 hover:bg-gray-700 text-gray-200' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-700'}`}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  aria-label="Select language"
                >
                  <span className="text-lg">{languageOptions.find(l => l.code === language)?.flag}</span>
                </motion.button>
              </Tooltip>
              
              <AnimatePresence>
                {isLangMenuOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    className={`absolute right-0 mt-2 w-36 rounded-lg shadow-xl overflow-hidden z-50 border
                      ${theme === 'dark'
                        ? 'bg-gray-800 border-gray-700'
                        : 'bg-white border-gray-200'}`}
                  >
                    {languageOptions.map((lang) => (
                      <motion.button
                        key={lang.code}
                        onClick={() => {
                          changeLanguage(lang.code);
                          setIsLangMenuOpen(false);
                        }}
                        className={`w-full text-left px-4 py-3 text-sm flex items-center space-x-2 border-b last:border-0
                          ${language === lang.code 
                            ? theme === 'dark'
                              ? 'bg-gray-700 text-white font-medium'
                              : 'bg-blue-50 text-blue-700 font-medium'
                            : theme === 'dark'
                              ? 'text-gray-300 hover:bg-gray-700'
                              : 'text-gray-700 hover:bg-gray-50'
                          } 
                          ${theme === 'dark'
                            ? 'border-gray-700'
                            : 'border-gray-100'}`}
                        whileHover={{ 
                          backgroundColor: theme === 'dark' ? '#374151' : '#f3f4f6',
                          transition: { duration: 0.2 }
                        }}
                        whileTap={{ scale: 0.98 }}
                      >
                        <span className="text-lg">{lang.flag}</span>
                        <span>{lang.name}</span>
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
            
            {/* Theme toggle button for mobile */}
            <Tooltip text={texts?.tooltipTheme || 'Toggle theme'}>
              <motion.button
                onClick={toggleTheme}
                className={`p-2 rounded-lg
                  ${theme === 'dark' 
                    ? 'bg-gray-800 hover:bg-gray-700 text-yellow-300' 
                    : 'bg-gray-100 hover:bg-gray-200 text-gray-700'}`}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="Toggle theme"
              >
                {theme === 'dark' ? (
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                  </svg>
                ) : (
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                  </svg>
                )}
              </motion.button>
            </Tooltip>
            
            {/* Hamburger menu button */}
            <motion.button 
              className="p-2 rounded-lg z-50 relative"
              onClick={toggleMenu}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <div className={`w-6 h-6 flex flex-col justify-center items-center ${
                theme === 'dark' 
                  ? 'bg-gray-800 hover:bg-gray-700 text-gray-200' 
                  : 'text-gray-700'
              }`}>
                <motion.span 
                  className={`block w-5 h-0.5 rounded-full transition-all duration-300 ${
                    theme === 'dark' ? 'bg-gray-300' : 'bg-gray-700'
                  } ${isMenuOpen ? 'absolute rotate-45' : '-translate-y-1.5'}`}
                />
                <motion.span 
                  className={`block w-5 h-0.5 rounded-full transition-all duration-300 ${
                    theme === 'dark' ? 'bg-gray-300' : 'bg-gray-700'
                  } ${isMenuOpen ? 'opacity-0' : 'opacity-100'}`}
                />
                <motion.span 
                  className={`block w-5 h-0.5 rounded-full transition-all duration-300 ${
                    theme === 'dark' ? 'bg-gray-300' : 'bg-gray-700'
                  } ${isMenuOpen ? 'absolute -rotate-45' : 'translate-y-1.5'}`}
                />
              </div>
            </motion.button>
          </div>
          
          {/* Desktop menu */}
          <div className="hidden md:flex items-center space-x-1">
            {filteredMenuItems.map((item, index) => (
              <Tooltip key={item.path} text={item.name || ''}>
                <motion.div
                  custom={index}
                  initial="closed"
                  animate="open"
                  variants={menuItemVariants}
                  whileHover="hover"
                  whileTap={{ scale: 0.95 }}
                >
                  <Link 
                    to={item.path} 
                    className={`px-3 py-2 rounded-md text-sm font-medium border transition-all duration-200 ${
                      location.pathname === item.path 
                        ? activeClass
                        : inactiveClass
                    }`}
                  >
                    {item.name}
                  </Link>
                </motion.div>
              </Tooltip>
            ))}
            
            {/* Language selector for desktop */}
            <div className="relative ml-1">
              <Tooltip text={texts?.tooltipLang || 'Change language'}>
                <motion.button
                  onClick={toggleLangMenu}
                  className={`px-3 py-2 rounded-md flex items-center text-sm font-medium
                    ${theme === 'dark' 
                      ? 'bg-gray-800 hover:bg-gray-700 text-gray-200' 
                      : 'bg-gray-100 hover:bg-gray-200 text-gray-700'}`}
                  variants={langButtonVariants}
                  whileHover="hover"
                  whileTap="tap"
                  aria-label="Select language"
                >
                  <span className="text-base mr-2">{languageOptions.find(l => l.code === language)?.flag}</span>
                  <span className="hidden sm:inline-block">{languageOptions.find(l => l.code === language)?.name}</span>
                  <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                </motion.button>
              </Tooltip>
              
              <AnimatePresence>
                {isLangMenuOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    className={`absolute right-0 mt-2 w-40 rounded-lg shadow-xl overflow-hidden z-50 border
                      ${theme === 'dark'
                        ? 'bg-gray-800 border-gray-700'
                        : 'bg-white border-gray-200'}`}
                  >
                    {languageOptions.map((lang) => (
                      <motion.button
                        key={lang.code}
                        onClick={() => {
                          changeLanguage(lang.code);
                          setIsLangMenuOpen(false);
                        }}
                        className={`w-full text-left px-4 py-3 text-sm flex items-center space-x-2 border-b last:border-0
                          ${language === lang.code 
                            ? theme === 'dark'
                              ? 'bg-gray-700 text-white font-medium'
                              : 'bg-blue-50 text-blue-700 font-medium'
                            : theme === 'dark'
                              ? 'text-gray-300 hover:bg-gray-700'
                              : 'text-gray-700 hover:bg-gray-50'
                          } 
                          ${theme === 'dark'
                            ? 'border-gray-700'
                            : 'border-gray-100'}`}
                        whileHover={{ 
                          backgroundColor: theme === 'dark' ? '#374151' : '#f3f4f6',
                          transition: { duration: 0.2 }
                        }}
                        whileTap={{ scale: 0.98 }}
                      >
                        <span className="text-lg">{lang.flag}</span>
                        <span>{lang.name}</span>
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
            
            {/* Theme toggle button */}
            <Tooltip text={texts?.tooltipTheme || 'Toggle theme'}>
              <motion.button
                onClick={toggleTheme}
                className={`p-2 rounded-md
                  ${theme === 'dark' 
                    ? 'bg-gray-800 hover:bg-gray-700 text-yellow-300' 
                    : 'bg-gray-100 hover:bg-gray-200 text-gray-700'}`}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="Toggle theme"
              >
                <motion.div
                  animate={{ rotate: theme === 'dark' ? 0 : 180 }}
                  transition={{ duration: 0.5 }}
                >
                  {theme === 'dark' ? (
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                    </svg>
                  ) : (
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                    </svg>
                  )}
                </motion.div>
              </motion.button>
            </Tooltip>
            
            {currentUser ? (
              <motion.div
                initial="closed"
                animate="open"
                variants={menuItemVariants}
                custom={filteredMenuItems.length}
              >
                <Tooltip text={texts?.tooltipLogout || 'Log out'}>
                  <motion.button 
                    onClick={logout}
                    className={`ml-1 px-4 py-2 rounded-md text-sm font-medium bg-gradient-to-r text-white shadow-sm
                      ${theme === 'dark'
                        ? 'from-rose-600 to-pink-600 hover:from-rose-700 hover:to-pink-700 shadow-rose-500/20'
                        : 'from-rose-500 to-pink-500 hover:from-rose-600 hover:to-pink-600 shadow-rose-300/30'}`}
                    whileHover={{ scale: 1.05, boxShadow: '0 4px 20px rgba(244, 63, 94, 0.25)' }}
                    whileTap={{ scale: 0.95 }}
                  >
                    {texts.logout || defaultMenuItems.logout}
                  </motion.button>
                </Tooltip>
              </motion.div>
            ) : (
              <div className="flex items-center space-x-2 ml-1">
                <motion.div
                  initial="closed"
                  animate="open"
                  variants={menuItemVariants}
                  custom={filteredMenuItems.length}
                >
                  <Tooltip text={texts?.tooltipLogin || 'Login'}>
                    <Link 
                      to="/login"
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-all duration-200
                        ${theme === 'dark'
                          ? 'bg-gray-800 hover:bg-gray-700 text-white'
                          : 'bg-white border border-gray-200 text-gray-700 hover:bg-gray-50'}`}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      {texts.login || defaultMenuItems.login}
                    </Link>
                  </Tooltip>
                </motion.div>
                <motion.div
                  initial="closed"
                  animate="open"
                  variants={menuItemVariants}
                  custom={filteredMenuItems.length + 1}
                >
                  <Tooltip text={texts?.tooltipRegister || 'Register'}>
                    <Link 
                      to="/register"
                      className={`px-4 py-2 rounded-md text-sm font-medium bg-gradient-to-r text-white shadow-sm
                        ${theme === 'dark'
                          ? 'from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 shadow-indigo-500/20'
                          : 'from-blue-500 to-indigo-500 hover:from-blue-600 hover:to-indigo-600 shadow-blue-300/30'}`}
                      whileHover={{ scale: 1.05, boxShadow: '0 4px 20px rgba(99, 102, 241, 0.25)' }}
                      whileTap={{ scale: 0.95 }}
                    >
                      {texts.register || defaultMenuItems.register}
                    </Link>
                  </Tooltip>
                </motion.div>
              </div>
            )}
          </div>
        </div>
        
        {/* Mobile menu */}
        <AnimatePresence>
          {isMenuOpen && (
            <motion.div 
              className={`md:hidden mt-2 rounded-lg p-3 overflow-hidden shadow-xl border
                ${theme === 'dark'
                  ? 'bg-gray-800 border-gray-700'
                  : 'bg-white border-gray-200'}`}
              initial="closed"
              animate="open"
              exit="closed"
              variants={mobileMenuVariants}
            >
              <div className="space-y-1">
                {filteredMenuItems.map((item, index) => (
                  <Tooltip key={item.path} text={item.name || ''}>
                    <motion.div
                      custom={index}
                      variants={menuItemVariants}
                      whileHover={{ 
                        scale: 1.02, 
                        backgroundColor: theme === 'dark' ? '#374151' : '#f3f4f6' 
                      }}
                      whileTap={{ scale: 0.98 }}
                      className="overflow-hidden rounded-md"
                    >
                      <Link 
                        to={item.path} 
                        className={`block px-4 py-3 text-sm font-medium rounded-md transition-colors border
                          ${location.pathname === item.path 
                            ? theme === 'dark'
                              ? 'bg-gradient-to-r from-blue-900 to-indigo-900 text-white border-transparent'
                              : 'bg-gradient-to-r from-blue-50 to-indigo-50 text-blue-700 border-blue-100'
                            : theme === 'dark'
                              ? 'text-gray-300 hover:text-white border-transparent'
                              : 'text-gray-700 hover:text-blue-700 border-transparent'
                          }`}
                      >
                        {item.name}
                      </Link>
                    </motion.div>
                  </Tooltip>
                ))}
                
                {currentUser ? (
                  <motion.div
                    variants={menuItemVariants}
                    custom={filteredMenuItems.length}
                    className="pt-2 mt-2 border-t"
                    style={{ 
                      borderColor: theme === 'dark' ? 'rgba(75, 85, 99, 0.5)' : 'rgba(229, 231, 235, 0.8)' 
                    }}
                  >
                    <Tooltip text={texts?.tooltipLogout || 'Log out'}>
                      <motion.button 
                        onClick={logout}
                        className={`w-full px-4 py-3 rounded-md text-sm font-medium bg-gradient-to-r text-white shadow-sm
                          ${theme === 'dark'
                            ? 'from-rose-600 to-pink-600'
                            : 'from-rose-500 to-pink-500'}`}
                        whileHover={{ scale: 1.02, boxShadow: '0 4px 12px rgba(244, 63, 94, 0.2)' }}
                        whileTap={{ scale: 0.98 }}
                      >
                        {texts.logout || defaultMenuItems.logout}
                      </motion.button>
                    </Tooltip>
                  </motion.div>
                ) : (
                  <motion.div
                    className="flex flex-col space-y-2 pt-2 mt-2 border-t"
                    style={{ 
                      borderColor: theme === 'dark' ? 'rgba(75, 85, 99, 0.5)' : 'rgba(229, 231, 235, 0.8)' 
                    }}
                  >
                    <motion.div
                      variants={menuItemVariants}
                      custom={filteredMenuItems.length}
                    >
                      <Tooltip text={texts?.tooltipLogin || 'Login'}>
                        <motion.div
                          whileHover={{ scale: 1.02 }}
                          whileTap={{ scale: 0.98 }}
                        >
                          <Link 
                            to="/login"
                            className={`block w-full px-4 py-3 rounded-md text-sm font-medium text-center
                              ${theme === 'dark'
                                ? 'bg-gray-700 text-white hover:bg-gray-600'
                                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}
                          >
                            {texts.login || defaultMenuItems.login}
                          </Link>
                        </motion.div>
                      </Tooltip>
                    </motion.div>
                    <motion.div
                      variants={menuItemVariants}
                      custom={filteredMenuItems.length + 1}
                    >
                      <Tooltip text={texts?.tooltipRegister || 'Register'}>
                        <motion.div
                          whileHover={{ scale: 1.02, boxShadow: '0 4px 12px rgba(59, 130, 246, 0.2)' }}
                          whileTap={{ scale: 0.98 }}
                        >
                          <Link 
                            to="/register"
                            className={`block w-full px-4 py-3 rounded-md text-sm font-medium text-center text-white 
                              ${theme === 'dark'
                                ? 'bg-gradient-to-r from-blue-600 to-indigo-600'
                                : 'bg-gradient-to-r from-blue-500 to-indigo-500'}`}
                          >
                            {texts.register || defaultMenuItems.register}
                          </Link>
                        </motion.div>
                      </Tooltip>
                    </motion.div>
                  </motion.div>
                )}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.nav>
  );
};

export default Navbar;
