import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { ThemeContext } from '../contexts/ThemeContext';
import { LanguageContext } from '../contexts/LanguageContext';

const Profile = () => {
  const { user, updateUserProfile, changeUserPassword, loading, error } = useContext(AuthContext);
  const { theme } = useContext(ThemeContext);
  const { texts, defaultTexts } = useContext(LanguageContext);
  
  const [nickname, setNickname] = useState(user?.nickname || '');
  const [showPasswordForm, setShowPasswordForm] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [updateSuccess, setUpdateSuccess] = useState('');
  
  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    
    // Check if user is trying to change password
    if (newPassword || confirmNewPassword) {
      if (!currentPassword) {
        return setPasswordError('Current password is required to change password');
      }
      
      if (newPassword !== confirmNewPassword) {
        return setPasswordError('New passwords do not match');
      }
      
      // Validate password strength if changing password
      if (newPassword) {
        const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
        if (!passwordRegex.test(newPassword)) {
          return setPasswordError('New password must be at least 8 characters and include uppercase, lowercase, number and special character');
        }
      }
    }
    
    // Check if anything is actually being updated
    if (nickname === user.nickname && !newPassword) {
      return setPasswordError('No changes detected');
    }
    
    try {
      setPasswordError('');
      setUpdateSuccess('');
      setLoading(true);
      
      await updateUserProfile(
        nickname !== user.nickname ? nickname : null, 
        newPassword ? currentPassword : null, 
        newPassword || null
      );
      
      setUpdateSuccess('Profile updated successfully');
      setCurrentPassword('');
      setNewPassword('');
      setConfirmNewPassword('');
    } catch (err) {
      setPasswordError(err.response?.data?.message || 'Failed to update profile');
    } finally {
      setLoading(false);
    }
  };
  
  const handleChangePassword = async (e) => {
    e.preventDefault();
    
    // Check if user is trying to change password
    if (newPassword || confirmNewPassword) {
      if (!currentPassword) {
        return setPasswordError('Current password is required to change password');
      }
      
      if (newPassword !== confirmNewPassword) {
        return setPasswordError('New passwords do not match');
      }
      
      // Validate password strength if changing password
      if (newPassword) {
        const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
        if (!passwordRegex.test(newPassword)) {
          return setPasswordError('New password must be at least 8 characters and include uppercase, lowercase, number and special character');
        }
      }
    }
    
    // Check if anything is actually being updated
    if (nickname === user.nickname && !newPassword) {
      return setPasswordError('No changes detected');
    }
    
    try {
      setPasswordError('');
      setUpdateSuccess('');
      setLoading(true);
      
      await changeUserPassword(
        nickname !== user.nickname ? nickname : null, 
        newPassword ? currentPassword : null, 
        newPassword || null
      );
      
      setUpdateSuccess('Password updated successfully');
      setCurrentPassword('');
      setNewPassword('');
      setConfirmNewPassword('');
    } catch (err) {
      setPasswordError(err.response?.data?.message || 'Failed to update password');
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className={`min-h-screen py-16 ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-800'}`}>
      <div className={`max-w-md mx-auto p-8 rounded-lg shadow-md ${
        theme === 'dark' ? 'bg-gray-800 border border-gray-700' : 'bg-white'
      }`}>
        <h1 className="text-2xl font-bold mb-6 text-center">{texts?.yourProfile || defaultTexts.yourProfile || "Your Profile"}</h1>
        
        {error && (
          <div className="mb-4 p-3 rounded-md bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-200">
            {error}
          </div>
        )}
        
        {updateSuccess && (
          <div className="mb-4 p-3 rounded-md bg-green-100 text-green-700 dark:bg-green-900 dark:text-green-200">
            {updateSuccess}
          </div>
        )}
        
        <div className="mb-4">
          <form onSubmit={handleUpdateProfile}>
            <div className="mb-4">
              <label className="block mb-2 font-medium">
                Email
              </label>
              <input
                disabled
                type="email"
                value={user?.email || ''}
                className={`block w-full rounded-md px-3 py-2 mb-1 ${
                  theme === 'dark' 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'bg-gray-100 border-gray-300 text-gray-900'
                } border`}
              />
            </div>
            <div className="mb-4">
              <label className="block mb-2 font-medium">
                {texts?.nickname || defaultTexts.nickname || "Nickname"}
              </label>
              <input
                type="text"
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
                className={`block w-full rounded-md px-3 py-2 mb-1 ${
                  theme === 'dark' 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'bg-gray-100 border-gray-300 text-gray-900'
                } border`}
              />
            </div>
            <button
              type="submit"
              disabled={loading}
              className={`w-full px-4 py-2 my-2 rounded-md ${
                theme === 'dark'
                  ? 'bg-blue-600 hover:bg-blue-700 text-white'
                  : 'bg-blue-500 hover:bg-blue-600 text-white'
              } transition-colors duration-200`}
            >
              {loading ? (texts?.updating || "Updating...") : (texts?.updateProfile || defaultTexts.updateProfile || "Update Profile")}
            </button>
          </form>
        </div>
        
        <div className="border-t border-gray-300 dark:border-gray-600 my-6 pt-6">
          <button
            onClick={() => setShowPasswordForm(!showPasswordForm)}
            className={`w-full px-4 py-2 my-2 rounded-md ${
              theme === 'dark'
                ? 'bg-gray-700 hover:bg-gray-600 text-white'
                : 'bg-gray-200 hover:bg-gray-300 text-gray-800'
            } transition-colors duration-200`}
          >
            {texts?.changePassword || defaultTexts.changePassword || "Change Password"}
          </button>
          
          {showPasswordForm && (
            <form onSubmit={handleChangePassword} className="mt-4">
              {passwordError && (
                <div className="mb-4 p-3 rounded-md bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-200">
                  {passwordError}
                </div>
              )}
              <div className="mb-4">
                <label className="block mb-2 font-medium">
                  {texts?.currentPassword || defaultTexts.currentPassword || "Current Password"}
                </label>
                <input
                  type="password"
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                  className={`block w-full rounded-md px-3 py-2 mb-1 ${
                    theme === 'dark' 
                      ? 'bg-gray-700 border-gray-600 text-white' 
                      : 'bg-gray-100 border-gray-300 text-gray-900'
                  } border`}
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block mb-2 font-medium">
                  {texts?.newPassword || defaultTexts.newPassword || "New Password"}
                </label>
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  className={`block w-full rounded-md px-3 py-2 mb-1 ${
                    theme === 'dark' 
                      ? 'bg-gray-700 border-gray-600 text-white' 
                      : 'bg-gray-100 border-gray-300 text-gray-900'
                  } border`}
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block mb-2 font-medium">
                  {texts?.confirmNewPassword || defaultTexts.confirmNewPassword || "Confirm New Password"}
                </label>
                <input
                  type="password"
                  value={confirmNewPassword}
                  onChange={(e) => setConfirmNewPassword(e.target.value)}
                  className={`block w-full rounded-md px-3 py-2 mb-1 ${
                    theme === 'dark' 
                      ? 'bg-gray-700 border-gray-600 text-white' 
                      : 'bg-gray-100 border-gray-300 text-gray-900'
                  } border`}
                  required
                />
              </div>
              <button
                type="submit"
                disabled={loading}
                className={`w-full px-4 py-2 my-2 rounded-md ${
                  theme === 'dark'
                    ? 'bg-green-600 hover:bg-green-700 text-white'
                    : 'bg-green-500 hover:bg-green-600 text-white'
                } transition-colors duration-200`}
              >
                {loading ? (texts?.updating || "Updating...") : (texts?.changePassword || defaultTexts.changePassword || "Change Password")}
              </button>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
