import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const UserStatistics = () => {
  const [statistics, setStatistics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // Animation refs
  const [ref1, inView1] = useInView({ threshold: 0.2, triggerOnce: true });
  const [ref2, inView2] = useInView({ threshold: 0.2, triggerOnce: true });
  
  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };
  
  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        type: "spring", 
        stiffness: 100, 
        damping: 15 
      }
    }
  };
  
  const countVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: { 
        type: "spring", 
        stiffness: 80 
      }
    }
  };
  
  const chartVariants = {
    hidden: { width: 0 },
    visible: width => ({
      width: `${width}%`,
      transition: { 
        duration: 1, 
        ease: "easeOut" 
      }
    })
  };
  
  useEffect(() => {
    const fetchStatistics = async () => {
      try {
        setLoading(true);
        setError('');
        
        const response = await axios.get('http://localhost:5000/api/news/statistics');
        setStatistics(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch statistics');
      } finally {
        setLoading(false);
      }
    };
    
    fetchStatistics();
  }, []);
  
  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-b from-blue-50 to-white py-10">
      <motion.div 
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-6xl mx-auto px-4 mb-8"
      >
        <div className="flex flex-col md:flex-row justify-between items-center mb-8">
          <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4 md:mb-0">User Statistics</h1>
          <motion.div
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <Link 
              to="/admin" 
              className="bg-white px-6 py-2 rounded-lg shadow-md hover:shadow-lg transition-all duration-300 flex items-center text-blue-600 border border-blue-200"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Back to Admin Panel
            </Link>
          </motion.div>
        </div>
        
        {error && (
          <motion.div 
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: "auto" }}
            transition={{ duration: 0.3 }}
            className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg mb-6"
          >
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm">{error}</p>
              </div>
            </div>
          </motion.div>
        )}
        
        {loading ? (
          <motion.div 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="flex justify-center items-center py-20"
          >
            <div className="flex flex-col items-center">
              <svg className="animate-spin h-10 w-10 text-blue-500 mb-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              <p className="text-gray-600">Loading statistics...</p>
            </div>
          </motion.div>
        ) : statistics ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <motion.div 
              ref={ref1}
              variants={containerVariants}
              initial="hidden"
              animate={inView1 ? "visible" : "hidden"}
              className="bg-white shadow-lg rounded-xl overflow-hidden"
            >
              <div className="px-6 py-4 bg-gradient-to-r from-blue-600 to-blue-700 border-b border-blue-700">
                <h3 className="text-lg font-semibold text-white flex items-center">
                  <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 12l3-3 3 3 4-4M8 21l4-4 4 4M3 4h18M4 4h16v12a1 1 0 01-1 1H5a1 1 0 01-1-1V4z" />
                  </svg>
                  General Statistics
                </h3>
              </div>
              <div className="p-6">
                <div className="grid grid-cols-2 gap-6">
                  <motion.div variants={itemVariants} className="bg-gradient-to-br from-blue-50 to-indigo-50 p-6 rounded-xl shadow-sm text-center border border-blue-100">
                    <motion.div variants={countVariants} className="text-4xl font-bold text-blue-600 mb-2">{statistics.userCount}</motion.div>
                    <div className="text-sm text-gray-600">
                      <div className="font-medium">Total Users</div>
                      <div className="text-xs mt-1">Registered accounts</div>
                    </div>
                  </motion.div>
                  
                  <motion.div variants={itemVariants} className="bg-gradient-to-br from-green-50 to-emerald-50 p-6 rounded-xl shadow-sm text-center border border-green-100">
                    <motion.div variants={countVariants} className="text-4xl font-bold text-green-600 mb-2">{statistics.checkCount}</motion.div>
                    <div className="text-sm text-gray-600">
                      <div className="font-medium">Total Checks</div>
                      <div className="text-xs mt-1">News checks performed</div>
                    </div>
                  </motion.div>
                </div>
                
                <motion.div variants={itemVariants} className="mt-8">
                  <div className="flex justify-between items-center mb-2">
                    <div className="text-sm font-medium text-gray-700">Fake News Percentage</div>
                    <div className="text-sm font-bold text-red-600">{statistics.avgFakePercentage}%</div>
                  </div>
                  <div className="relative pt-1">
                    <div className="overflow-hidden h-3 mb-4 text-xs flex rounded-full bg-red-100">
                      <motion.div 
                        custom={statistics.avgFakePercentage}
                        variants={chartVariants}
                        className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-gradient-to-r from-red-500 to-red-600 rounded-full"
                      ></motion.div>
                    </div>
                  </div>
                  <div className="text-xs text-gray-600 italic">
                    Average percentage of news detected as fake
                  </div>
                </motion.div>
                
                <motion.div variants={itemVariants} className="mt-8">
                  <div className="flex justify-between items-center mb-2">
                    <div className="text-sm font-medium text-gray-700">Real News Percentage</div>
                    <div className="text-sm font-bold text-green-600">{100 - statistics.avgFakePercentage}%</div>
                  </div>
                  <div className="relative pt-1">
                    <div className="overflow-hidden h-3 mb-4 text-xs flex rounded-full bg-green-100">
                      <motion.div 
                        custom={100 - statistics.avgFakePercentage}
                        variants={chartVariants}
                        className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-gradient-to-r from-green-500 to-green-600 rounded-full"
                      ></motion.div>
                    </div>
                  </div>
                  <div className="text-xs text-gray-600 italic">
                    Average percentage of news detected as real
                  </div>
                </motion.div>
              </div>
            </motion.div>
            
            <motion.div 
              ref={ref2}
              variants={containerVariants}
              initial="hidden"
              animate={inView2 ? "visible" : "hidden"}
              className="bg-white shadow-lg rounded-xl overflow-hidden"
            >
              <div className="px-6 py-4 bg-gradient-to-r from-green-600 to-emerald-600 border-b border-green-700">
                <h3 className="text-lg font-semibold text-white flex items-center">
                  <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197M13 7a4 4 0 11-8 0 4 4 0 018 0z" />
                  </svg>
                  Top 5 Active Users
                </h3>
              </div>
              <div className="p-6">
                {statistics.topUsers.length === 0 ? (
                  <motion.div 
                    variants={itemVariants}
                    className="text-center py-8 text-gray-600"
                  >
                    <svg className="w-16 h-16 text-gray-300 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                    <p>No data available yet</p>
                  </motion.div>
                ) : (
                  <div className="space-y-5">
                    {statistics.topUsers.map((user, index) => {
                      const percentage = ((user.check_count / statistics.checkCount) * 100).toFixed(1);
                      return (
                        <motion.div 
                          key={index} 
                          variants={itemVariants}
                          className="hover:bg-gray-50 p-3 rounded-lg transition-colors duration-150"
                        >
                          <div className="flex items-center mb-2">
                            <div className={`w-9 h-9 rounded-full flex items-center justify-center mr-3 ${
                              index === 0 ? 'bg-yellow-100 text-yellow-700' : 
                              index === 1 ? 'bg-gray-100 text-gray-700' : 
                              index === 2 ? 'bg-orange-100 text-orange-700' : 
                              'bg-blue-100 text-blue-700'
                            }`}>
                              <span className="font-bold text-sm">{index + 1}</span>
                            </div>
                            <div className="flex-grow">
                              <div className="font-medium text-gray-800">{user.nickname}</div>
                              <div className="text-xs text-gray-500">{user.check_count} checks performed</div>
                            </div>
                            <div className="w-16 text-right">
                              <div className="text-sm font-medium text-blue-600">
                                {percentage}%
                              </div>
                            </div>
                          </div>
                          
                          <div className="ml-12">
                            <div className="overflow-hidden h-2 text-xs flex rounded-full bg-blue-100">
                              <motion.div 
                                custom={parseFloat(percentage)}
                                variants={chartVariants}
                                className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-blue-500 rounded-full"
                              ></motion.div>
                            </div>
                          </div>
                        </motion.div>
                      );
                    })}
                  </div>
                )}
                <motion.div 
                  variants={itemVariants}
                  className="mt-6 pt-4 border-t border-gray-200 text-center"
                >
                  <div className="text-sm text-gray-500">
                    Each user's contribution to the total news checks
                  </div>
                </motion.div>
              </div>
            </motion.div>
          </div>
        ) : null}
      </motion.div>
    </div>
  );
};

export default UserStatistics;
