import React, { useState } from 'react';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import Lottie from 'lottie-react';
import checkAnimation from '../assets/check-animation.json';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const CheckNews = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { theme } = useTheme();
  const { texts = {} } = useLanguage() || {};

  // Default text values if translations aren't loaded yet
  const defaultTexts = {
    detectFakeNews: 'Detect Fake News',
    submitNewsPrompt: 'Submit news content to analyze its credibility using our advanced AI technology',
    checkNewsTitle: 'Check News',
    newsTitle: 'News Title',
    newsDescription: 'News Description',
    enterNewsTitle: 'Enter the title of the news article',
    enterNewsDescription: 'Enter the content or description of the news article',
    analyzeNews: 'Analyze News',
    analyzing: 'Analyzing...',
    analysisResult: 'Analysis Result',
    real: 'REAL',
    fake: 'FAKE',
    confidenceScore: 'Confidence Score',
    analyzedNews: 'Analyzed News',
    detailedAnalysis: 'Detailed Analysis',
    aiInfoText: 'Our AI uses advanced natural language processing to analyze news content. While highly accurate, we recommend using this tool as one of many resources for verifying information.'
  };

  // Animation refs and state
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const formVariants = {
    hidden: { 
      opacity: 0, 
      y: 50 
    },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        type: "spring",
        damping: 20,
        stiffness: 100
      }
    }
  };

  const inputVariants = {
    focus: { 
      scale: 1.02, 
      boxShadow: theme === 'dark' 
        ? "0px 5px 20px rgba(0,0,0,0.5)" 
        : "0px 5px 20px rgba(0,0,0,0.15)"
    },
    blur: { 
      scale: 1, 
      boxShadow: "0px 0px 0px rgba(0,0,0,0.1)"
    }
  };

  const resultVariants = {
    hidden: { 
      opacity: 0, 
      y: 30,
      scale: 0.95 
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: { 
        type: "spring", 
        damping: 20, 
        stiffness: 100 
      }
    },
    exit: { 
      opacity: 0, 
      y: -20, 
      transition: { 
        duration: 0.3 
      } 
    }
  };

  const buttonVariants = {
    hover: { 
      scale: 1.05,
      boxShadow: theme === 'dark'
        ? "0px 5px 15px rgba(29, 78, 216, 0.6)"
        : "0px 5px 15px rgba(37, 99, 235, 0.4)"
    },
    tap: { 
      scale: 0.95 
    },
    disabled: {
      opacity: 0.7,
      cursor: "not-allowed"
    }
  };

  const progressVariants = {
    hidden: { width: 0 },
    visible: width => ({
      width: `${width}%`,
      transition: { 
        duration: 0.8, 
        ease: "easeOut" 
      }
    })
  };

  // Staggered container animation
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { type: "spring", stiffness: 100, damping: 15 }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setError('');
      setResult(null);
      setLoading(true);

      const response = await axios.post('http://localhost:5000/api/news/check', {
        title,
        description
      });

      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to check news');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`flex flex-col items-center py-10 min-h-screen ${
      theme === 'dark' 
        ? 'bg-gradient-to-b from-gray-900 to-gray-800 text-white' 
        : 'bg-gradient-to-b from-blue-50 to-white'
    } transition-colors duration-300`}>
      <motion.div 
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-4xl mb-8 text-center"
      >
        <h1 className="text-3xl md:text-4xl font-bold text-gray-800 dark:text-white mb-2">{texts.detectFakeNews || defaultTexts.detectFakeNews}</h1>
        <p className="text-lg text-gray-600 dark:text-gray-300 max-w-2xl mx-auto">
          {texts.submitNewsPrompt || defaultTexts.submitNewsPrompt}
        </p>
      </motion.div>

      <div className="w-full max-w-4xl flex flex-col md:flex-row gap-8 px-4">
        <motion.div 
          ref={ref}
          className={`bg-white dark:bg-gray-800 shadow-lg dark:shadow-xl rounded-2xl px-8 pt-6 pb-8 mb-4 flex-1 ${
            theme === 'dark' ? 'shadow-blue-900/10' : ''
          } transition-colors duration-300`}
          variants={formVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <h2 className="text-2xl font-bold mb-6 text-center text-blue-800 dark:text-blue-400">{texts.checkNewsTitle || defaultTexts.checkNewsTitle}</h2>

          {error && (
            <motion.div 
              className="bg-red-100 dark:bg-red-900/30 border-l-4 border-red-500 text-red-700 dark:text-red-300 p-4 rounded mb-4"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.4 }}
            >
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <p className="text-sm">{error}</p>
                </div>
              </div>
            </motion.div>
          )}

          <motion.form 
            onSubmit={handleSubmit} 
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            className="space-y-6"
          >
            <motion.div variants={itemVariants}>
              <label className="block text-gray-700 dark:text-gray-300 text-sm font-bold mb-2" htmlFor="title">
                {texts.newsTitle || defaultTexts.newsTitle}
              </label>
              <motion.input
                className="shadow-sm appearance-none border border-gray-300 dark:border-gray-600 rounded-lg w-full py-3 px-4 text-gray-700 dark:text-white dark:bg-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400 focus:border-transparent transition-all duration-200"
                id="title"
                type="text"
                placeholder={texts.enterNewsTitle || defaultTexts.enterNewsTitle}
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                variants={inputVariants}
                initial="blur"
                whileFocus="focus"
                whileTap="focus"
                required
              />
            </motion.div>

            <motion.div variants={itemVariants}>
              <label className="block text-gray-700 dark:text-gray-300 text-sm font-bold mb-2" htmlFor="description">
                {texts.newsDescription || defaultTexts.newsDescription}
              </label>
              <motion.textarea
                className="shadow-sm appearance-none border border-gray-300 dark:border-gray-600 rounded-lg w-full py-3 px-4 text-gray-700 dark:text-white dark:bg-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400 focus:border-transparent transition-all duration-200"
                id="description"
                placeholder={texts.enterNewsDescription || defaultTexts.enterNewsDescription}
                rows="7"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                variants={inputVariants}
                initial="blur"
                whileFocus="focus"
                whileTap="focus"
                required
              />
            </motion.div>

            <motion.div variants={itemVariants} className="flex items-center justify-center pt-2">
              <motion.button
                className={`${
                  theme === 'dark'
                    ? 'bg-blue-700 hover:bg-blue-600' 
                    : 'bg-blue-600 hover:bg-blue-700'
                } text-white font-bold py-3 px-6 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400 focus:ring-opacity-50 w-full md:w-auto transition-all duration-200`}
                type="submit"
                disabled={loading}
                variants={buttonVariants}
                whileHover={!loading ? "hover" : "disabled"}
                whileTap={!loading ? "tap" : "disabled"}
                initial={{ opacity: 0.9 }}
                animate={{ opacity: 1 }}
              >
                <span className="flex items-center justify-center">
                  {loading ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      {texts.analyzing || defaultTexts.analyzing}
                    </>
                  ) : (
                    <>
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                      </svg>
                      {texts.analyzeNews || defaultTexts.analyzeNews}
                    </>
                  )}
                </span>
              </motion.button>
            </motion.div>
          </motion.form>
        </motion.div>

        <AnimatePresence mode="wait">
          {result && (
            <motion.div 
              key="result"
              className="flex-1"
              variants={resultVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <div className={`bg-white dark:bg-gray-800 shadow-lg dark:shadow-xl rounded-2xl p-6 mb-4 border-t-8 ${
                result.result 
                  ? 'border-green-500' 
                  : 'border-red-500'
              } transition-colors duration-300`}>
                <div className="flex justify-between items-center mb-6">
                  <h3 className="text-2xl font-bold text-gray-800 dark:text-white">{texts.analysisResult || defaultTexts.analysisResult}</h3>
                  <div className={`px-4 py-1 rounded-full text-white font-semibold ${result.result ? 'bg-green-500' : 'bg-red-500'}`}>
                    {result.result ? (texts.real || defaultTexts.real) : (texts.fake || defaultTexts.fake)}
                  </div>
                </div>

                <div className="mb-6">
                  <div className="flex justify-between mb-2">
                    <span className="font-medium text-gray-700 dark:text-gray-300">{texts.confidenceScore || defaultTexts.confidenceScore}</span>
                    <span className="font-bold text-blue-700 dark:text-blue-400">{result.confidencePercentage}%</span>
                  </div>
                  <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-4 overflow-hidden">
                    <motion.div 
                      className={`h-4 rounded-full ${result.result ? 'bg-green-500' : 'bg-red-500'}`}
                      custom={result.confidencePercentage}
                      variants={progressVariants}
                      initial="hidden"
                      animate="visible"
                    ></motion.div>
                  </div>
                </div>

                <div className="mb-6">
                  <div className="font-medium text-gray-700 dark:text-gray-300 mb-2">{texts.analyzedNews || defaultTexts.analyzedNews}</div>
                  <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg border border-gray-200 dark:border-gray-600">
                    <div className="font-semibold text-gray-800 dark:text-white mb-2">{result.title}</div>
                    <div className="text-gray-600 dark:text-gray-300">{result.description}</div>
                  </div>
                </div>

                {result.analysis && (
                  <div>
                    <div className="font-medium text-gray-700 dark:text-gray-300 mb-2">{texts.detailedAnalysis || defaultTexts.detailedAnalysis}</div>
                    <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg border border-gray-200 dark:border-gray-600 whitespace-pre-line text-sm text-gray-700 dark:text-gray-300 max-h-64 overflow-y-auto">
                      {result.analysis}
                    </div>
                  </div>
                )}

                <motion.div 
                  className="mt-6 flex justify-center"
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ 
                    type: "spring", 
                    stiffness: 260, 
                    damping: 20,
                    delay: 0.5
                  }}
                >
                  {result.result ? (
                    <div className="w-32">
                      <Lottie animationData={checkAnimation} loop={false} />
                    </div>
                  ) : (
                    <motion.div 
                      initial={{ rotate: 0 }}
                      animate={{ 
                        rotate: [0, 5, -5, 5, -5, 0],
                        scale: [1, 1.1, 1] 
                      }}
                      transition={{ 
                        duration: 0.8,
                        times: [0, 0.2, 0.4, 0.6, 0.8, 1],
                        repeat: 1, 
                        repeatDelay: 0.5
                      }}
                      className="w-16 h-16 bg-red-100 dark:bg-red-900/30 rounded-full flex items-center justify-center text-red-500"
                    >
                      <svg className="w-10 h-10" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                      </svg>
                    </motion.div>
                  )}
                </motion.div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      <motion.div 
        className="w-full max-w-4xl px-4 mt-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.5 }}
      >
        <div className={`bg-blue-50 dark:bg-blue-900/20 border-l-4 border-blue-500 text-blue-700 dark:text-blue-300 p-4 rounded-lg shadow-sm transition-colors duration-300`}>
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-blue-500 dark:text-blue-400" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2h-1V9a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm">
                {texts.aiInfoText || defaultTexts.aiInfoText}
              </p>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default CheckNews;
