import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import Lottie from 'lottie-react';
import animationData from '../assets/news-animation.json';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const Home = () => {
  const { currentUser } = useContext(AuthContext);
  const { theme } = useTheme();
  const { texts } = useLanguage() || {};
  
  // Default translations if context not loaded yet
  const defaultTexts = {
    detectFakeNews: 'Detect Fake News',
    withAI: 'With AI',
    aiPowered: 'Harness the power of AI to identify misinformation and protect yourself from fake news.',
    checkNewsNow: 'Check News Now',
    getStarted: 'Get Started',
    learnMore: 'Learn More',
    howItWorks: 'How It Works',
    enterNewsDetails: 'Enter News Details',
    inputNewsDescription: 'Input the title and description of the news article you want to verify',
    aiAnalysis: 'AI Analysis',
    aiAnalysisDescription: 'Our advanced AI analyzes the content and checks for signs of misinformation',
    getResults: 'Get Results',
    getResultsDescription: 'Receive a detailed report on whether the news is real or fake, with confidence score',
    whyChooseUs: 'Why Choose Us',
    accuracy: 'Accuracy',
    accuracyDescription: 'Powered by advanced AI technology for reliable results',
    easyToUse: 'Easy to Use',
    easyToUseDescription: 'Simple interface that anyone can understand',
    historyTracking: 'History Tracking',
    historyTrackingDescription: 'Keep track of all your previous news checks',
    fightMisinformation: 'Fight Misinformation',
    fightMisinformationDescription: 'Help promote truth and accuracy in media',
    byTheNumbers: 'By The Numbers',
    accuracyRate: 'Accuracy Rate',
    articlesAnalyzed: 'Articles Analyzed',
    users: 'Users',
    support: 'Support',
    testimonials: 'Testimonials',
    readyToStart: 'Ready to start checking news?',
    createAccount: 'Create your free account today'
  };
  
  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.2
      }
    }
  };
  
  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.6, ease: "easeOut" }
    }
  };

  const fadeInUpVariants = {
    hidden: { y: 60, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.8, ease: "easeOut" }
    }
  };

  const staggerContainer = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const pulse = {
    scale: [1, 1.05, 1],
    transition: { 
      duration: 2,
      ease: "easeInOut",
      repeat: Infinity,
      repeatType: "reverse"
    }
  };

  const scaleUpVariants = {
    hidden: { scale: 0.9, opacity: 0 },
    visible: {
      scale: 1,
      opacity: 1,
      transition: { duration: 0.5, ease: "easeOut" }
    }
  };

  const slideInLeftVariants = {
    hidden: { x: -100, opacity: 0 },
    visible: {
      x: 0,
      opacity: 1,
      transition: { duration: 0.6, ease: "easeOut" }
    }
  };
  
  const slideInRightVariants = {
    hidden: { x: 100, opacity: 0 },
    visible: {
      x: 0,
      opacity: 1,
      transition: { duration: 0.6, ease: "easeOut" }
    }
  };

  // Features data
  const howItWorks = [
    {
      title: texts?.enterNewsDetails || defaultTexts.enterNewsDetails,
      description: texts?.inputNewsDescription || defaultTexts.inputNewsDescription,
      icon: (
        <svg className="w-10 h-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
        </svg>
      )
    },
    {
      title: texts?.aiAnalysis || defaultTexts.aiAnalysis,
      description: texts?.aiAnalysisDescription || defaultTexts.aiAnalysisDescription,
      icon: (
        <svg className="w-10 h-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
        </svg>
      )
    },
    {
      title: texts?.getResults || defaultTexts.getResults,
      description: texts?.getResultsDescription || defaultTexts.getResultsDescription,
      icon: (
        <svg className="w-10 h-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      )
    }
  ];

  const benefits = [
    {
      title: texts?.accuracy || defaultTexts.accuracy,
      description: texts?.accuracyDescription || defaultTexts.accuracyDescription,
      icon: (
        <svg className="w-10 h-10 text-primary-500 dark:text-primary-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      )
    },
    {
      title: texts?.easyToUse || defaultTexts.easyToUse,
      description: texts?.easyToUseDescription || defaultTexts.easyToUseDescription,
      icon: (
        <svg className="w-10 h-10 text-primary-500 dark:text-primary-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 3v4M3 5h4M6 17v4m-2-2h4m5-16l2.286 6.857L21 12l-5.714 2.143L13 21l-2.286-6.857L5 12l5.714-2.143L13 3z" />
        </svg>
      )
    },
    {
      title: texts?.historyTracking || defaultTexts.historyTracking,
      description: texts?.historyTrackingDescription || defaultTexts.historyTrackingDescription,
      icon: (
        <svg className="w-10 h-10 text-primary-500 dark:text-primary-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      )
    },
    {
      title: texts?.fightMisinformation || defaultTexts.fightMisinformation,
      description: texts?.fightMisinformationDescription || defaultTexts.fightMisinformationDescription,
      icon: (
        <svg className="w-10 h-10 text-primary-500 dark:text-primary-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 21v-4m0 0V5a2 2 0 012-2h6.5l1 1H21l-3 6 3 6h-8.5l-1-1H5a2 2 0 00-2 2zm9-13.5V9" />
        </svg>
      )
    }
  ];

  // Stats data
  const stats = [
    { value: "99.7%", label: texts?.accuracyRate || defaultTexts.accuracyRate },
    { value: "10K+", label: texts?.articlesAnalyzed || defaultTexts.articlesAnalyzed },
    { value: "5K+", label: texts?.users || defaultTexts.users },
    { value: "24/7", label: texts?.support || defaultTexts.support }
  ];

  // Testimonials
  const testimonials = [
    {
      quote: "This tool has completely changed how I verify news before sharing. It's become an essential part of my daily media consumption.",
      author: "Sarah J.",
      role: "Journalist"
    },
    {
      quote: "As an educator, I teach my students to use Fake News Detector to develop critical thinking and media literacy skills.",
      author: "Michael T.",
      role: "University Professor"
    },
    {
      quote: "The accuracy of the AI detection is impressive. It has helped our organization maintain credibility in all our communications.",
      author: "Emily R.",
      role: "Communications Director"
    }
  ];

  // Use intersection observer for animations
  const [ref1, inView1] = useInView({ threshold: 0.2, triggerOnce: true });
  const [ref2, inView2] = useInView({ threshold: 0.2, triggerOnce: true });
  const [ref3, inView3] = useInView({ threshold: 0.2, triggerOnce: true });
  const [ref4, inView4] = useInView({ threshold: 0.2, triggerOnce: true });

  return (
    <div className="bg-gray-50 dark:bg-dark-900 min-h-screen transition-colors duration-300">
      {/* Hero Section */}
      <section className="relative overflow-hidden bg-gradient-to-r from-primary-700 via-primary-600 to-secondary-600 dark:from-dark-900 dark:via-primary-900 dark:to-secondary-900 text-white pt-28 pb-20">
        {/* Background decoration */}
        <div className="absolute inset-0 z-0 overflow-hidden">
          <svg className="absolute bottom-0 left-0 w-full" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
            <path fill="rgba(255, 255, 255, 0.1)" fillOpacity="1" d="M0,192L48,181.3C96,171,192,149,288,149.3C384,149,480,171,576,197.3C672,224,768,256,864,240C960,224,1056,160,1152,149.3C1248,139,1344,181,1392,202.7L1440,224L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
          </svg>
          <motion.div 
            className="absolute top-10 right-10 w-64 h-64 rounded-full bg-primary-500/20 backdrop-blur-3xl"
            animate={{
              scale: [1, 1.2, 1],
              x: [0, 30, 0],
              y: [0, -30, 0],
            }}
            transition={{
              duration: 15,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "reverse"
            }}
          />
          <motion.div 
            className="absolute bottom-20 left-20 w-32 h-32 rounded-full bg-secondary-500/20 backdrop-blur-3xl"
            animate={{
              scale: [1, 1.5, 1],
              x: [0, -20, 0],
              y: [0, 20, 0],
            }}
            transition={{
              duration: 12,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "reverse",
              delay: 1
            }}
          />
        </div>
        
        <div className="container mx-auto px-4 z-10 relative">
          <motion.div 
            className="flex flex-col md:flex-row items-center justify-between gap-12"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.div 
              className="md:w-1/2 text-center md:text-left mb-8 md:mb-0 px-4"
              variants={slideInLeftVariants}
            >
              <motion.h1 
                className="text-4xl md:text-5xl lg:text-6xl font-extrabold mb-6 text-white text-shadow-lg"
                variants={itemVariants}
              >
                <span className="block">{texts?.detectFakeNews || defaultTexts.detectFakeNews}</span>
                <span className="block text-transparent bg-clip-text bg-gradient-to-r from-white to-blue-200">
                  {texts?.withAI || defaultTexts.withAI}
                </span>
              </motion.h1>
              
              <motion.p 
                className="text-xl md:text-2xl text-white/80 mb-8 font-light"
                variants={itemVariants}
              >
                {texts?.aiPowered || defaultTexts.aiPowered}
              </motion.p>
              
              <motion.div
                className="flex flex-col sm:flex-row justify-center md:justify-start gap-4"
                variants={itemVariants}
              >
                {currentUser ? (
                  <motion.div
                    whileHover={{ scale: 1.05, y: -5 }}
                    whileTap={{ scale: 0.95 }}
                    className="animate-float"
                  >
                    <Link to="/check-news" className="btn-primary inline-block py-4 px-8 rounded-xl text-lg shadow-blue-glow">
                      {texts?.checkNewsNow || defaultTexts.checkNewsNow}
                    </Link>
                  </motion.div>
                ) : (
                  <>
                    <motion.div
                      whileHover={{ scale: 1.05, y: -5 }}
                      whileTap={{ scale: 0.95 }}
                      className="animate-float"
                    >
                      <Link to="/register" className="btn-primary inline-block py-4 px-8 rounded-xl text-lg shadow-blue-glow">
                        {texts?.getStarted || defaultTexts.getStarted}
                      </Link>
                    </motion.div>
                    <motion.div
                      whileHover={{ scale: 1.05, y: -5 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      <a href="#how-it-works" className="btn-outline inline-block py-4 px-8 rounded-xl text-lg border-2 border-white/30 hover:border-white/80 backdrop-blur-sm bg-white/10">
                        {texts?.learnMore || defaultTexts.learnMore}
                      </a>
                    </motion.div>
                  </>
                )}
              </motion.div>
            </motion.div>
            
            <motion.div 
              className="md:w-1/2 flex justify-center md:justify-end"
              variants={slideInRightVariants}
            >
              <motion.div 
                className="relative w-full max-w-md glass-card p-4 rounded-2xl overflow-hidden"
                animate={pulse}
              >
                <Lottie animationData={animationData} className="w-full" />
                <motion.div 
                  className="absolute -bottom-2 -right-2 -left-2 h-24 bg-gradient-to-t from-primary-600/90 to-transparent backdrop-blur-sm"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 1 }}
                />
              </motion.div>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 bg-white dark:bg-dark-800">
        <div className="container mx-auto px-4">
          <motion.div 
            className="grid grid-cols-2 md:grid-cols-4 gap-6"
            ref={ref1}
            variants={staggerContainer}
            initial="hidden"
            animate={inView1 ? "visible" : "hidden"}
          >
            {stats.map((stat, index) => (
              <motion.div 
                key={index} 
                className="card p-6 text-center hover:shadow-blue-glow/30 dark:hover:shadow-blue-glow/20"
                variants={fadeInUpVariants}
                whileHover={{ y: -8, scale: 1.03 }}
                transition={{ duration: 0.3 }}
              >
                <motion.div 
                  className="text-4xl md:text-5xl font-bold text-gradient mb-3"
                  initial={{ scale: 0.8 }}
                  animate={{ scale: 1 }}
                  transition={{ duration: 0.5, delay: 0.2 * index }}
                >
                  {stat.value}
                </motion.div>
                <div className="text-gray-700 dark:text-gray-300 font-medium">{stat.label}</div>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </section>

      {/* How It Works Section */}
      <section id="how-it-works" className="py-20 bg-gradient-to-br from-primary-100 to-secondary-100 dark:from-dark-900 dark:to-dark-800">
        <div className="container mx-auto px-4">
          <motion.div 
            ref={ref2}
            initial="hidden"
            animate={inView2 ? "visible" : "hidden"}
            variants={staggerContainer}
            className="text-center mb-16"
          >
            <motion.h2 
              variants={fadeInUpVariants}
              className="text-3xl md:text-4xl font-bold mb-6 text-gray-900 dark:text-white text-shadow"
            >
              {texts?.howItWorks || defaultTexts.howItWorks}
            </motion.h2>
            <motion.div 
              variants={fadeInUpVariants}
              className="h-1 w-24 bg-gradient-to-r from-primary-500 to-primary-300 rounded-full mx-auto"
            ></motion.div>
          </motion.div>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 xl:gap-12">
            {howItWorks.map((item, index) => (
              <motion.div 
                key={index}
                className="card-hover glass-card"
                variants={scaleUpVariants}
                custom={index}
                whileHover={{ y: -10 }}
              >
                <div className="relative overflow-hidden p-8 rounded-xl">
                  {/* Decorative blob in the background */}
                  <div className="absolute -right-10 -bottom-10 w-40 h-40 rounded-full bg-gradient-to-br from-primary-500/30 to-secondary-500/30 blur-2xl"></div>
                  
                  <div className="relative z-10">
                    <div className="bg-gradient-to-br from-primary-500 to-primary-700 dark:from-primary-600 dark:to-primary-800 w-16 h-16 rounded-2xl flex items-center justify-center mb-6 shadow-blue-glow/30">
                      {item.icon}
                    </div>
                    <h3 className="text-xl font-bold mb-3 text-gray-900 dark:text-white">{item.title}</h3>
                    <p className="text-gray-600 dark:text-gray-300">{item.description}</p>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Benefits Section */}
      <section className="py-20 bg-white dark:bg-dark-800">
        <div className="container mx-auto px-4">
          <motion.div 
            ref={ref3}
            initial="hidden"
            animate={inView3 ? "visible" : "hidden"}
            variants={staggerContainer}
            className="text-center mb-16"
          >
            <motion.h2 
              variants={fadeInUpVariants}
              className="text-3xl md:text-4xl font-bold mb-6 text-gray-900 dark:text-white text-shadow"
            >
              {texts?.whyChooseUs || defaultTexts.whyChooseUs}
            </motion.h2>
            <motion.div 
              variants={fadeInUpVariants}
              className="h-1 w-24 bg-gradient-to-r from-primary-500 to-primary-300 rounded-full mx-auto"
            ></motion.div>
          </motion.div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {benefits.map((item, index) => (
              <motion.div 
                key={index}
                className="card p-6 text-center"
                variants={scaleUpVariants}
                custom={index}
                whileHover={{ y: -5, boxShadow: "0 10px 30px -5px rgba(0, 0, 0, 0.1)" }}
              >
                <div className="mx-auto w-20 h-20 rounded-full bg-primary-100 dark:bg-primary-900/30 flex items-center justify-center mb-5">
                  {item.icon}
                </div>
                <h3 className="text-xl font-bold mb-3 text-gray-900 dark:text-white">{item.title}</h3>
                <p className="text-gray-600 dark:text-gray-400">{item.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="py-20 bg-gray-50 dark:bg-dark-900">
        <div className="container mx-auto px-4">
          <motion.div 
            initial="hidden"
            animate="visible"
            variants={staggerContainer}
            className="text-center mb-16"
          >
            <motion.h2 
              variants={fadeInUpVariants}
              className="text-3xl md:text-4xl font-bold mb-6 text-gray-900 dark:text-white text-shadow"
            >
              {texts?.testimonials || defaultTexts.testimonials}
            </motion.h2>
            <motion.div 
              variants={fadeInUpVariants}
              className="h-1 w-24 bg-gradient-to-r from-primary-500 to-primary-300 rounded-full mx-auto"
            ></motion.div>
          </motion.div>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {testimonials.map((testimonial, index) => (
              <motion.div 
                key={index}
                className="card p-6 relative"
                variants={scaleUpVariants}
                custom={index}
                whileHover={{ y: -5, boxShadow: "0 10px 30px -5px rgba(0, 0, 0, 0.1)" }}
              >
                <div className="absolute top-6 left-6 text-5xl text-primary-300 dark:text-primary-700 opacity-50">"</div>
                <div className="relative z-10">
                  <p className="text-gray-700 dark:text-gray-300 mb-6 italic">{testimonial.quote}</p>
                  <div className="flex items-center">
                    <div className="bg-gradient-to-br from-primary-500 to-primary-700 dark:from-primary-600 dark:to-primary-800 w-10 h-10 rounded-full flex items-center justify-center mr-3 text-white font-bold">
                      {testimonial.author.charAt(0)}
                    </div>
                    <div>
                      <h4 className="font-bold text-gray-900 dark:text-white">{testimonial.author}</h4>
                      <p className="text-gray-500 dark:text-gray-400 text-sm">{testimonial.role}</p>
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-gradient-to-r from-primary-600 to-secondary-600 dark:from-primary-900 dark:to-secondary-900 text-white relative overflow-hidden">
        {/* Background decoration */}
        <div className="absolute inset-0 z-0 overflow-hidden">
          <svg className="absolute top-0 left-0 w-full transform rotate-180" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
            <path fill="rgba(255, 255, 255, 0.1)" fillOpacity="1" d="M0,192L48,181.3C96,171,192,149,288,149.3C384,149,480,171,576,197.3C672,224,768,256,864,240C960,224,1056,160,1152,149.3C1248,139,1344,181,1392,202.7L1440,224L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
          </svg>
        </div>
        
        <div className="container mx-auto px-4 text-center relative z-10">
          <motion.h2 
            ref={ref4}
            initial="hidden"
            animate={inView4 ? "visible" : "hidden"}
            variants={fadeInUpVariants}
            className="text-3xl md:text-4xl lg:text-5xl font-bold mb-6 text-shadow-lg"
          >
            {texts?.readyToStart || defaultTexts.readyToStart}
          </motion.h2>
          <motion.p 
            variants={fadeInUpVariants}
            className="text-xl mb-10 text-white/80 max-w-2xl mx-auto font-light"
          >
            {texts?.createAccount || defaultTexts.createAccount}
          </motion.p>
          
          {!currentUser && (
            <motion.div
              whileHover={{ scale: 1.05, y: -5 }}
              whileTap={{ scale: 0.95 }}
              variants={fadeInUpVariants}
              className="animate-float"
            >
              <Link to="/register" className="inline-block py-4 px-10 rounded-xl bg-white text-primary-700 font-bold shadow-xl hover:shadow-2xl transition-all duration-300 text-lg">
                {texts?.getStarted || defaultTexts.getStarted}
              </Link>
            </motion.div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Home;
