import React from 'react';
import { Link } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import { motion } from 'framer-motion';

const Footer = () => {
  const { theme } = useTheme();
  const { texts } = useLanguage() || {};
  
  // Default text values if translations aren't loaded yet
  const defaultTexts = {
    appDescription: 'Fighting misinformation with cutting-edge AI technology',
    quickLinks: 'Quick Links',
    contactUs: 'Contact Us',
    allRightsReserved: 'All rights reserved.',
    privacyPolicy: 'Privacy Policy',
    termsOfService: 'Terms of Service',
    home: 'Home',
    checkNews: 'Check News',
    history: 'History',
    login: 'Login',
    register: 'Register'
  };
  
  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.1,
        delayChildren: 0.3
      }
    }
  };
  
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { 
      y: 0, 
      opacity: 1,
      transition: { 
        type: "spring", 
        stiffness: 100,
        damping: 10
      }
    }
  };
  
  const linkVariants = {
    hover: { 
      scale: 1.05, 
      color: theme === 'dark' ? '#60a5fa' : '#2563eb',
      transition: { duration: 0.2 } 
    },
    tap: { scale: 0.95 }
  };
  
  return (
    <footer className={`${
      theme === 'dark' 
        ? 'bg-gray-900 text-gray-200 border-t border-gray-800' 
        : 'bg-white text-gray-700 border-t border-gray-100'
    } mt-auto`}>
      <div className="container mx-auto px-4 py-8">
        <motion.div 
          className="grid grid-cols-1 md:grid-cols-3 gap-8"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, margin: "-100px" }}
        >
          {/* About */}
          <motion.div variants={itemVariants}>
            <div className="mb-4 flex items-center">
              <div className={`w-8 h-8 rounded-lg flex items-center justify-center mr-2 overflow-hidden 
                ${theme === 'dark' 
                  ? 'bg-gradient-to-br from-blue-600 to-indigo-700' 
                  : 'bg-gradient-to-br from-blue-500 to-indigo-600'}`}
              >
                <motion.svg 
                  className="w-5 h-5 text-white" 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24" 
                  xmlns="http://www.w3.org/2000/svg"
                  animate={{ rotate: 360 }}
                  transition={{ duration: 8, repeat: Infinity, ease: "linear" }}
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" 
                  />
                </motion.svg>
              </div>
              <span className="text-lg font-bold">Fake News Detector</span>
            </div>
            <p className={`mb-4 text-sm ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
              {texts?.appDescription || defaultTexts.appDescription}
            </p>
            <div className="flex space-x-3">
              <motion.a 
                href="https://twitter.com" 
                target="_blank" 
                rel="noopener noreferrer"
                className={`p-2 rounded-full ${
                  theme === 'dark' ? 'bg-gray-800 hover:bg-gray-700' : 'bg-gray-100 hover:bg-gray-200'
                }`}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="Twitter"
              >
                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path d="M23.953 4.57a10 10 0 01-2.825.775 4.958 4.958 0 002.163-2.723c-.951.555-2.005.959-3.127 1.184a4.92 4.92 0 00-8.384 4.482C7.69 8.095 4.067 6.13 1.64 3.162a4.822 4.822 0 00-.666 2.475c0 1.71.87 3.213 2.188 4.096a4.904 4.904 0 01-2.228-.616v.06a4.923 4.923 0 003.946 4.827 4.996 4.996 0 01-2.212.085 4.936 4.936 0 004.604 3.417 9.867 9.867 0 01-6.102 2.105c-.39 0-.779-.023-1.17-.067a13.995 13.995 0 007.557 2.209c9.053 0 13.998-7.496 13.998-13.985 0-.21 0-.42-.015-.63A9.935 9.935 0 0024 4.59z"/>
                </svg>
              </motion.a>
              <motion.a 
                href="https://facebook.com" 
                target="_blank" 
                rel="noopener noreferrer"
                className={`p-2 rounded-full ${
                  theme === 'dark' ? 'bg-gray-800 hover:bg-gray-700' : 'bg-gray-100 hover:bg-gray-200'
                }`}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="Facebook"
              >
                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                </svg>
              </motion.a>
              <motion.a 
                href="https://linkedin.com" 
                target="_blank" 
                rel="noopener noreferrer"
                className={`p-2 rounded-full ${
                  theme === 'dark' ? 'bg-gray-800 hover:bg-gray-700' : 'bg-gray-100 hover:bg-gray-200'
                }`}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                aria-label="LinkedIn"
              >
                <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
                </svg>
              </motion.a>
            </div>
          </motion.div>
          
          {/* Links */}
          <motion.div variants={itemVariants}>
            <h3 className="text-sm font-semibold uppercase tracking-wider mb-4">
              {texts?.quickLinks || defaultTexts.quickLinks}
            </h3>
            <ul className="space-y-2">
              <li>
                <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                  <Link to="/" className={`text-sm hover:underline ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
                    {texts?.home || defaultTexts.home}
                  </Link>
                </motion.div>
              </li>
              <li>
                <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                  <Link to="/check-news" className={`text-sm hover:underline ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
                    {texts?.checkNews || defaultTexts.checkNews}
                  </Link>
                </motion.div>
              </li>
              <li>
                <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                  <Link to="/history" className={`text-sm hover:underline ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
                    {texts?.history || defaultTexts.history}
                  </Link>
                </motion.div>
              </li>
              <li>
                <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                  <Link to="/login" className={`text-sm hover:underline ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
                    {texts?.login || defaultTexts.login}
                  </Link>
                </motion.div>
              </li>
              <li>
                <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                  <Link to="/register" className={`text-sm hover:underline ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
                    {texts?.register || defaultTexts.register}
                  </Link>
                </motion.div>
              </li>
            </ul>
          </motion.div>
          
          {/* Contact */}
          <motion.div variants={itemVariants}>
            <h3 className="text-sm font-semibold uppercase tracking-wider mb-4">
              {texts?.contactUs || defaultTexts.contactUs}
            </h3>
            <ul className="space-y-2">
              <li className="flex items-start">
                <svg className={`w-5 h-5 mr-2 ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                </svg>
                <motion.a 
                  href="mailto:info@fakenewsdetector.ai"
                  className={`text-sm hover:underline ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}
                  whileHover={{ color: theme === 'dark' ? '#60a5fa' : '#2563eb' }}
                >
                  info@fakenewsdetector.ai
                </motion.a>
              </li>
              <li className="flex items-start">
                <svg className={`w-5 h-5 mr-2 ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                </svg>
                <motion.a 
                  href="tel:+7-777-777-7777"
                  className={`text-sm hover:underline ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}
                  whileHover={{ color: theme === 'dark' ? '#60a5fa' : '#2563eb' }}
                >
                  +7-777-777-7777
                </motion.a>
              </li>
              <li className="flex items-start">
                <svg className={`w-5 h-5 mr-2 ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
                <span className={`text-sm ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
                  Almaty, Kazakhstan
                </span>
              </li>
            </ul>
          </motion.div>
        </motion.div>
        
        {/* Bottom */}
        <motion.div 
          className={`mt-8 pt-6 border-t ${theme === 'dark' ? 'border-gray-800' : 'border-gray-100'} flex flex-col md:flex-row justify-between items-center text-sm`}
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ delay: 0.5, duration: 0.6 }}
        >
          <p className={theme === 'dark' ? 'text-gray-500' : 'text-gray-500'}>
            &copy; {new Date().getFullYear()} Fake News Detector. {texts?.allRightsReserved || defaultTexts.allRightsReserved}
          </p>
          <div className="flex space-x-6 mt-4 md:mt-0">
            <motion.div whileHover="hover" whileTap="tap" variants={linkVariants}>
              <Link to="/privacy-policy" className={`text-xs ${theme === 'dark' ? 'text-gray-500' : 'text-gray-500'} hover:underline`}>
                {texts?.privacyPolicy || defaultTexts.privacyPolicy}
              </Link>
            </motion.div>
            <motion.div whileHover="hover" whileTap="tap" variants={linkVariants}>
              <Link to="/terms-of-service" className={`text-xs ${theme === 'dark' ? 'text-gray-500' : 'text-gray-500'} hover:underline`}>
                {texts?.termsOfService || defaultTexts.termsOfService}
              </Link>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </footer>
  );
};

export default Footer;
