import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import { motion, AnimatePresence } from 'framer-motion';
import Tooltip from './Tooltip';

const Navbar = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const { theme, toggleTheme } = useTheme();
  const { language, changeLanguage, texts = {} } = useLanguage() || {};
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLangMenuOpen, setIsLangMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrolled]);

  // Close menus when route changes
  useEffect(() => {
    setIsMenuOpen(false);
    setIsLangMenuOpen(false);
  }, [location.pathname]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
    if (isLangMenuOpen) setIsLangMenuOpen(false);
  };

  const toggleLangMenu = () => {
    setIsLangMenuOpen(!isLangMenuOpen);
    if (isMenuOpen) setIsMenuOpen(false);
  };

  // Animation variants
  const navbarVariants = {
    initial: {
      backgroundColor: theme === 'dark' ? 'rgba(17, 24, 39, 0)' : 'rgba(255, 255, 255, 0)',
      boxShadow: '0 0 0 rgba(0, 0, 0, 0)',
      y: 0
    },
    scrolled: {
      backgroundColor: theme === 'dark' ? 'rgba(17, 24, 39, 0.85)' : 'rgba(255, 255, 255, 0.85)',
      backdropFilter: 'blur(8px)',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
      y: 0
    }
  };

  const logoVariants = {
    initial: { opacity: 0, x: -20 },
    animate: { opacity: 1, x: 0, transition: { duration: 0.5 } }
  };

  const menuItemVariants = {
    closed: { opacity: 0, y: -10 },
    open: (i) => ({ 
      opacity: 1, 
      y: 0, 
      transition: { delay: i * 0.1, duration: 0.3 } 
    }),
  };

  const mobileMenuVariants = {
    closed: { 
      opacity: 0, 
      height: 0,
      transition: {
        duration: 0.3,
        when: "afterChildren",
        staggerChildren: 0.05,
        staggerDirection: -1
      }
    },
    open: { 
      opacity: 1, 
      height: 'auto',
      transition: {
        duration: 0.3,
        when: "beforeChildren",
        staggerChildren: 0.05,
        staggerDirection: 1
      }
    }
  };

  const langButtonVariants = {
    hover: { scale: 1.1 },
    tap: { scale: 0.95 }
  };

  // Language options
  const languageOptions = [
    { code: 'en', name: 'English', flag: 'ðŸ‡ºðŸ‡¸' },
    { code: 'ru', name: 'Ð ÑƒÑÑÐºÐ¸Ð¹', flag: 'ðŸ‡·ðŸ‡º' },
    { code: 'kz', name: 'ÒšÐ°Ð·Ð°Ò›ÑˆÐ°', flag: 'ðŸ‡°ðŸ‡¿' }
  ];

  // Default menu item names in case translations aren't loaded yet
  const defaultMenuItems = {
    home: 'Home',
    contact: 'Contact',
    checkNews: 'Check News',
    history: 'History',
    profile: 'Profile',
    admin: 'Admin',
    login: 'Login',
    register: 'Register',
    logout: 'Logout'
  };

  const menuItems = [
    { name: texts.home || defaultMenuItems.home, path: '/', showAlways: true },
    { name: texts.contact || defaultMenuItems.contact, path: '/contact', showAlways: true },
    { name: texts.checkNews || defaultMenuItems.checkNews, path: '/check-news', showLoggedIn: true },
    { name: texts.history || defaultMenuItems.history, path: '/history', showLoggedIn: true },
    { name: texts.profile || defaultMenuItems.profile, path: '/profile', showLoggedIn: true },
    { name: texts.admin || defaultMenuItems.admin, path: '/admin', showAdmin: true }
  ];

  // Filter menu items based on auth state
  const filteredMenuItems = menuItems.filter(item => 
    (item.showAlways) || 
    (currentUser && item.showLoggedIn) || 
    (currentUser?.role === 'admin' && item.showAdmin)
  );

  return (
    <motion.nav 
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
        scrolled ? 'py-2' : 'py-4'
      }`}
      initial="initial"
      animate={scrolled ? "scrolled" : "initial"}
      variants={navbarVariants}
    >
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center">
          <motion.div
            variants={logoVariants}
            initial="initial"
            animate="animate"
          >
            <Link to="/" className="flex items-center gap-2">
              <div className="w-10 h-10 bg-white dark:bg-dark-700 rounded-full flex items-center justify-center shadow-blue-glow dark:shadow-blue-glow/30">
                <svg 
                  className="w-6 h-6 text-primary-600 dark:text-primary-400" 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24" 
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" 
                  />
                </svg>
              </div>
              <span className="text-xl font-bold text-gray-900 dark:text-white text-gradient animate-fade-in">Fake News Detector</span>
            </Link>
          </motion.div>

          {/* Mobile menu buttons */}
          <div className="flex items-center md:hidden">
            {/* Language selector for mobile */}
            <div className="relative mr-3">
              <Tooltip text={texts?.tooltipLang || 'Change language'}>
                <motion.button
                  onClick={toggleLangMenu}
                  className="p-2 rounded-full bg-primary-600 dark:bg-dark-700 text-white shadow-blue-glow/30"
                  whileHover={langButtonVariants.hover}
                  whileTap={langButtonVariants.tap}
                  aria-label="Select language"
                >
                  <span className="text-lg">{languageOptions.find(l => l.code === language)?.flag}</span>
                </motion.button>
              </Tooltip>

              <AnimatePresence>
                {isLangMenuOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    className="absolute right-0 mt-2 w-36 glass-card rounded-xl shadow-xl overflow-hidden z-50"
                  >
                    {languageOptions.map(lang => (
                      <motion.button
                        key={lang.code}
                        onClick={() => {
                          changeLanguage(lang.code);
                          setIsLangMenuOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm flex items-center space-x-2 ${
                          language === lang.code 
                            ? 'bg-primary-100 dark:bg-primary-900/30 text-primary-700 dark:text-primary-300' 
                            : 'text-gray-700 dark:text-gray-300'
                        } hover:bg-primary-50 dark:hover:bg-dark-700`}
                        whileHover={{ backgroundColor: theme === 'dark' ? 'rgba(31, 41, 55, 0.5)' : 'rgba(239, 246, 255, 0.5)' }}
                      >
                        <span className="text-lg">{lang.flag}</span>
                        <span>{lang.name}</span>
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Theme toggle button for mobile */}
            <Tooltip text={texts?.tooltipTheme || 'Toggle theme'}>
              <motion.button
                onClick={toggleTheme}
                className="ml-2 p-2 rounded-full bg-primary-600 dark:bg-dark-700 text-white shadow-blue-glow/30"
                whileHover={langButtonVariants.hover}
                whileTap={langButtonVariants.tap}
                aria-label="Toggle theme"
              >
                {theme === 'dark' ? (
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                  </svg>
                ) : (
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                  </svg>
                )}
              </motion.button>
            </Tooltip>

            {/* Menu toggle button */}
            <motion.button
              onClick={toggleMenu}
              className="ml-3 p-2 rounded-full bg-primary-600 dark:bg-dark-700 text-white shadow-blue-glow/30"
              whileHover={langButtonVariants.hover}
              whileTap={langButtonVariants.tap}
              aria-label={isMenuOpen ? "Close menu" : "Open menu"}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                {isMenuOpen ? (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                ) : (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                )}
              </svg>
            </motion.button>
          </div>

          {/* Desktop menu */}
          <div className="hidden md:flex md:items-center space-x-4">
            {filteredMenuItems.map((item, index) => (
              <motion.div
                key={item.path}
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                <Tooltip text={item.name}>
                  <Link 
                    to={item.path}
                    className={`px-3 py-2 rounded-lg text-sm font-medium transition-all duration-300 ${
                      location.pathname === item.path
                        ? 'bg-primary-100 dark:bg-primary-900/30 text-primary-700 dark:text-primary-300'
                        : 'text-gray-700 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 hover:bg-primary-50 dark:hover:bg-dark-700/50'
                    }`}
                  >
                    {item.name}
                  </Link>
                </Tooltip>
              </motion.div>
            ))}
          </div>

          {/* Desktop auth & theme & language */}
          <div className="hidden md:flex md:items-center space-x-3">
            {/* Language selector for desktop */}
            <div className="relative">
              <Tooltip text={texts?.tooltipLang || 'Change language'}>
                <motion.button
                  onClick={toggleLangMenu}
                  className="p-2 rounded-full bg-primary-600 dark:bg-dark-700 text-white shadow-blue-glow/30"
                  whileHover={langButtonVariants.hover}
                  whileTap={langButtonVariants.tap}
                  aria-label="Select language"
                >
                  <span className="text-lg">{languageOptions.find(l => l.code === language)?.flag}</span>
                </motion.button>
              </Tooltip>

              <AnimatePresence>
                {isLangMenuOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    className="absolute right-0 mt-2 w-36 glass-card rounded-xl shadow-xl overflow-hidden z-50"
                  >
                    {languageOptions.map(lang => (
                      <motion.button
                        key={lang.code}
                        onClick={() => {
                          changeLanguage(lang.code);
                          setIsLangMenuOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm flex items-center space-x-2 ${
                          language === lang.code 
                            ? 'bg-primary-100 dark:bg-primary-900/30 text-primary-700 dark:text-primary-300' 
                            : 'text-gray-700 dark:text-gray-300'
                        } hover:bg-primary-50 dark:hover:bg-dark-700`}
                        whileHover={{ backgroundColor: theme === 'dark' ? 'rgba(31, 41, 55, 0.5)' : 'rgba(239, 246, 255, 0.5)' }}
                      >
                        <span className="text-lg">{lang.flag}</span>
                        <span>{lang.name}</span>
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Theme toggle button */}
            <Tooltip text={texts?.tooltipTheme || 'Toggle theme'}>
              <motion.button
                onClick={toggleTheme}
                className="p-2 rounded-full bg-primary-600 dark:bg-dark-700 text-white shadow-blue-glow/30"
                whileHover={langButtonVariants.hover}
                whileTap={langButtonVariants.tap}
                aria-label="Toggle theme"
              >
                {theme === 'dark' ? (
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                  </svg>
                ) : (
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                  </svg>
                )}
              </motion.button>
            </Tooltip>

            {currentUser ? (
              <Tooltip text={texts?.tooltipLogout || 'Log out'}>
                <motion.button 
                  onClick={logout}
                  className="btn-danger animate-slide-in-right"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {texts.logout || defaultMenuItems.logout}
                </motion.button>
              </Tooltip>
            ) : (
              <>
                <Tooltip text={texts?.tooltipLogin || 'Login'}>
                  <motion.div
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.1 }}
                  >
                    <Link 
                      to="/login"
                      className="btn-outline animate-slide-in-right"
                    >
                      {texts.login || defaultMenuItems.login}
                    </Link>
                  </motion.div>
                </Tooltip>
                <Tooltip text={texts?.tooltipRegister || 'Register'}>
                  <motion.div
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.2 }}
                  >
                    <Link 
                      to="/register"
                      className="btn-primary animate-slide-in-right"
                    >
                      {texts.register || defaultMenuItems.register}
                    </Link>
                  </motion.div>
                </Tooltip>
              </>
            )}
          </div>
        </div>
      </div>

      {/* Mobile menu dropdown */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.div
            initial="closed"
            animate="open"
            exit="closed"
            variants={mobileMenuVariants}
            className="md:hidden bg-white dark:bg-dark-700 shadow-xl dark:shadow-none mt-2 rounded-b-2xl overflow-hidden"
          >
            <div className="px-4 py-5 space-y-3">
              {filteredMenuItems.map((item, index) => (
                <motion.div 
                  key={item.path}
                  variants={menuItemVariants}
                  custom={index}
                >
                  <Tooltip text={item.name}>
                    <Link 
                      to={item.path}
                      className={`block px-3 py-2 rounded-lg text-base ${
                        location.pathname === item.path
                          ? 'bg-primary-100 dark:bg-primary-900/30 text-primary-700 dark:text-primary-300 font-medium'
                          : 'text-gray-700 dark:text-gray-300 hover:bg-primary-50 dark:hover:bg-dark-600'
                      }`}
                    >
                      {item.name}
                    </Link>
                  </Tooltip>
                </motion.div>
              ))}

              {currentUser ? (
                <motion.div
                  variants={menuItemVariants}
                  custom={filteredMenuItems.length}
                >
                  <Tooltip text={texts?.tooltipLogout || 'Log out'}>
                    <button 
                      onClick={logout}
                      className="w-full px-3 py-2 rounded-lg bg-danger-500 hover:bg-danger-600 text-white text-left transition-all duration-200"
                    >
                      {texts.logout || defaultMenuItems.logout}
                    </button>
                  </Tooltip>
                </motion.div>
              ) : (
                <>
                  <motion.div
                    variants={menuItemVariants}
                    custom={filteredMenuItems.length}
                  >
                    <Tooltip text={texts?.tooltipLogin || 'Login'}>
                      <Link 
                        to="/login"
                        className="block w-full px-3 py-2 rounded-lg bg-white dark:bg-dark-600 text-primary-600 dark:text-white border border-primary-500 dark:border-primary-400"
                      >
                        {texts.login || defaultMenuItems.login}
                      </Link>
                    </Tooltip>
                  </motion.div>
                  <motion.div
                    variants={menuItemVariants}
                    custom={filteredMenuItems.length + 1}
                  >
                    <Tooltip text={texts?.tooltipRegister || 'Register'}>
                      <Link 
                        to="/register"
                        className="block w-full px-3 py-2 rounded-lg bg-primary-600 hover:bg-primary-700 dark:bg-primary-700 dark:hover:bg-primary-800 text-white"
                      >
                        {texts.register || defaultMenuItems.register}
                      </Link>
                    </Tooltip>
                  </motion.div>
                </>
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.nav>
  );
};

export default Navbar;
