import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import { motion, AnimatePresence } from 'framer-motion';

const Navbar = () => {
  const { currentUser, logout } = useContext(AuthContext);
  const { theme, toggleTheme } = useTheme();
  const { language, changeLanguage, texts = {} } = useLanguage() || {};
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLangMenuOpen, setIsLangMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();
  
  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrolled]);

  // Close menus when route changes
  useEffect(() => {
    setIsMenuOpen(false);
    setIsLangMenuOpen(false);
  }, [location.pathname]);
  
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
    if (isLangMenuOpen) setIsLangMenuOpen(false);
  };

  const toggleLangMenu = () => {
    setIsLangMenuOpen(!isLangMenuOpen);
    if (isMenuOpen) setIsMenuOpen(false);
  };

  // Animation variants
  const navbarVariants = {
    initial: {
      backgroundColor: theme === 'dark' ? 'rgba(17, 24, 39, 0)' : 'rgba(37, 99, 235, 0)',
      boxShadow: '0 0 0 rgba(0, 0, 0, 0)'
    },
    scrolled: {
      backgroundColor: theme === 'dark' ? 'rgba(17, 24, 39, 0.95)' : 'rgba(37, 99, 235, 0.95)',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)'
    }
  };

  const logoVariants = {
    initial: { opacity: 0, x: -20 },
    animate: { opacity: 1, x: 0, transition: { duration: 0.5 } }
  };

  const menuItemVariants = {
    closed: { opacity: 0, y: -10 },
    open: (i) => ({ 
      opacity: 1, 
      y: 0, 
      transition: { delay: i * 0.1, duration: 0.3 } 
    }),
  };

  const mobileMenuVariants = {
    closed: { 
      opacity: 0, 
      height: 0,
      transition: {
        duration: 0.3,
        when: "afterChildren",
        staggerChildren: 0.05,
        staggerDirection: -1
      }
    },
    open: { 
      opacity: 1, 
      height: 'auto',
      transition: {
        duration: 0.3,
        when: "beforeChildren",
        staggerChildren: 0.05,
        staggerDirection: 1
      }
    }
  };

  const langButtonVariants = {
    hover: { scale: 1.1 },
    tap: { scale: 0.95 }
  };

  // Language options
  const languageOptions = [
    { code: 'en', name: 'English', flag: '🇺🇸' },
    { code: 'ru', name: 'Русский', flag: '🇷🇺' },
    { code: 'kz', name: 'Қазақша', flag: '🇰🇿' }
  ];

  // Default menu item names in case translations aren't loaded yet
  const defaultMenuItems = {
    home: 'Home',
    contact: 'Contact',
    checkNews: 'Check News',
    history: 'History',
    profile: 'Profile',
    admin: 'Admin',
    login: 'Login',
    register: 'Register',
    logout: 'Logout'
  };

  const menuItems = [
    { name: texts.home || defaultMenuItems.home, path: '/', showAlways: true },
    { name: texts.contact || defaultMenuItems.contact, path: '/contact', showAlways: true },
    { name: texts.checkNews || defaultMenuItems.checkNews, path: '/check-news', showLoggedIn: true },
    { name: texts.history || defaultMenuItems.history, path: '/history', showLoggedIn: true },
    { name: texts.profile || defaultMenuItems.profile, path: '/profile', showLoggedIn: true },
    { name: texts.admin || defaultMenuItems.admin, path: '/admin', showAdmin: true }
  ];

  // Filter menu items based on auth state
  const filteredMenuItems = menuItems.filter(item => 
    (item.showAlways) || 
    (currentUser && item.showLoggedIn) || 
    (currentUser?.role === 'admin' && item.showAdmin)
  );

  return (
    <motion.nav 
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
        scrolled ? 'py-2' : 'py-3'
      } ${theme === 'dark' ? 'dark:bg-gray-900' : ''}`}
      initial="initial"
      animate={scrolled ? "scrolled" : "initial"}
      variants={navbarVariants}
    >
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center">
          <motion.div
            variants={logoVariants}
            initial="initial"
            animate="animate"
          >
            <Link to="/" className="flex items-center gap-2">
              <div className="w-10 h-10 bg-white dark:bg-gray-800 rounded-full flex items-center justify-center">
                <svg 
                  className="w-6 h-6 text-blue-600 dark:text-blue-400" 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24" 
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" 
                  />
                </svg>
              </div>
              <span className="text-xl font-bold text-white dark:text-white">Fake News Detector</span>
            </Link>
          </motion.div>
          
          {/* Mobile menu buttons */}
          <div className="flex items-center md:hidden">
            {/* Language selector for mobile */}
            <div className="relative mr-3">
              <motion.button
                onClick={toggleLangMenu}
                className="p-2 rounded-full bg-blue-700 dark:bg-gray-700 text-white"
                whileTap={{ scale: 0.9 }}
                aria-label="Select language"
              >
                <span className="text-lg">{languageOptions.find(l => l.code === language)?.flag}</span>
              </motion.button>
              
              <AnimatePresence>
                {isLangMenuOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    className="absolute right-0 mt-2 w-36 bg-white dark:bg-gray-800 rounded-md shadow-lg z-50"
                  >
                    {languageOptions.map((lang) => (
                      <motion.button
                        key={lang.code}
                        onClick={() => {
                          changeLanguage(lang.code);
                          setIsLangMenuOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm flex items-center space-x-2 ${
                          language === lang.code 
                            ? 'bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-300' 
                            : 'text-gray-700 dark:text-gray-300'
                        } hover:bg-blue-50 dark:hover:bg-gray-700`}
                        whileHover={{ backgroundColor: theme === 'dark' ? '#1f2937' : '#eff6ff' }}
                      >
                        <span className="text-lg">{lang.flag}</span>
                        <span>{lang.name}</span>
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
            
            {/* Theme toggle button for mobile */}
            <motion.button
              onClick={toggleTheme}
              className="mr-3 p-2 rounded-full bg-blue-700 dark:bg-gray-700 text-white"
              whileTap={{ scale: 0.9 }}
              aria-label="Toggle theme"
            >
              {theme === 'dark' ? (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
              ) : (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
              )}
            </motion.button>
            
            <motion.button 
              className="z-50 relative"
              onClick={toggleMenu}
              whileTap={{ scale: 0.9 }}
            >
              <div className="w-7 h-7 flex flex-col justify-center items-center">
                <motion.span 
                  className={`block w-6 h-0.5 bg-white rounded-full transition-all duration-300 ${isMenuOpen ? 'absolute rotate-45' : '-translate-y-1.5'}`}
                />
                <motion.span 
                  className={`block w-6 h-0.5 bg-white rounded-full transition-all duration-300 ${isMenuOpen ? 'opacity-0' : 'opacity-100'}`}
                />
                <motion.span 
                  className={`block w-6 h-0.5 bg-white rounded-full transition-all duration-300 ${isMenuOpen ? 'absolute -rotate-45' : 'translate-y-1.5'}`}
                />
              </div>
            </motion.button>
          </div>
          
          {/* Desktop menu */}
          <div className="hidden md:flex items-center space-x-1">
            {filteredMenuItems.map((item, index) => (
              <motion.div
                key={item.path}
                custom={index}
                initial="closed"
                animate="open"
                variants={menuItemVariants}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Link 
                  to={item.path} 
                  className={`px-3 py-2 rounded-md text-white hover:bg-blue-700 dark:hover:bg-gray-700 transition-colors ${
                    location.pathname === item.path 
                      ? 'bg-blue-700 dark:bg-gray-700' 
                      : ''
                  }`}
                >
                  {item.name}
                </Link>
              </motion.div>
            ))}
            
            {/* Language selector for desktop */}
            <div className="relative ml-3">
              <motion.button
                onClick={toggleLangMenu}
                className="px-3 py-2 rounded-md bg-blue-700 dark:bg-gray-700 text-white flex items-center"
                variants={langButtonVariants}
                whileHover="hover"
                whileTap="tap"
                aria-label="Select language"
              >
                <span className="text-lg mr-2">{languageOptions.find(l => l.code === language)?.flag}</span>
                <span className="hidden sm:inline-block">{languageOptions.find(l => l.code === language)?.name}</span>
                <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </motion.button>
              
              <AnimatePresence>
                {isLangMenuOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    className="absolute right-0 mt-2 w-40 bg-white dark:bg-gray-800 rounded-md shadow-lg z-50"
                  >
                    {languageOptions.map((lang) => (
                      <motion.button
                        key={lang.code}
                        onClick={() => {
                          changeLanguage(lang.code);
                          setIsLangMenuOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm flex items-center space-x-2 ${
                          language === lang.code 
                            ? 'bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-300' 
                            : 'text-gray-700 dark:text-gray-300'
                        } hover:bg-blue-50 dark:hover:bg-gray-700`}
                        whileHover={{ backgroundColor: theme === 'dark' ? '#1f2937' : '#eff6ff' }}
                      >
                        <span className="text-lg">{lang.flag}</span>
                        <span>{lang.name}</span>
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
            
            {/* Theme toggle button */}
            <motion.button
              onClick={toggleTheme}
              className="ml-2 p-2 rounded-full bg-blue-700 dark:bg-gray-700 text-white"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              aria-label="Toggle theme"
            >
              {theme === 'dark' ? (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
              ) : (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
              )}
            </motion.button>
            
            {currentUser ? (
              <motion.div
                initial="closed"
                animate="open"
                variants={menuItemVariants}
                custom={filteredMenuItems.length}
              >
                <motion.button 
                  onClick={logout}
                  className="ml-2 px-4 py-2 rounded-md bg-gradient-to-r from-red-500 to-pink-600 text-white hover:from-red-600 hover:to-pink-700 transition-all"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {texts.logout || defaultMenuItems.logout}
                </motion.button>
              </motion.div>
            ) : (
              <div className="flex items-center space-x-2">
                <motion.div
                  initial="closed"
                  animate="open"
                  variants={menuItemVariants}
                  custom={filteredMenuItems.length}
                >
                  <Link 
                    to="/login"
                    className="px-4 py-2 rounded-md bg-white dark:bg-gray-700 text-blue-600 dark:text-white hover:bg-gray-100 dark:hover:bg-gray-600 transition-colors"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    {texts.login || defaultMenuItems.login}
                  </Link>
                </motion.div>
                <motion.div
                  initial="closed"
                  animate="open"
                  variants={menuItemVariants}
                  custom={filteredMenuItems.length + 1}
                >
                  <Link 
                    to="/register"
                    className="px-4 py-2 rounded-md bg-gradient-to-r from-blue-800 to-indigo-900 dark:from-blue-700 dark:to-indigo-800 text-white hover:from-blue-900 hover:to-indigo-950 dark:hover:from-blue-800 dark:hover:to-indigo-900 transition-colors shadow-lg"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    {texts.register || defaultMenuItems.register}
                  </Link>
                </motion.div>
              </div>
            )}
          </div>
        </div>
        
        {/* Mobile menu */}
        <AnimatePresence>
          {isMenuOpen && (
            <motion.div 
              className="md:hidden mt-3 bg-blue-800 dark:bg-gray-800 rounded-lg p-4 overflow-hidden"
              initial="closed"
              animate="open"
              exit="closed"
              variants={mobileMenuVariants}
            >
              <div className="space-y-2">
                {filteredMenuItems.map((item, index) => (
                  <motion.div
                    key={item.path}
                    custom={index}
                    variants={menuItemVariants}
                  >
                    <Link 
                      to={item.path} 
                      className={`block px-3 py-2 rounded-md transition-colors ${
                        location.pathname === item.path 
                          ? 'bg-blue-600 dark:bg-gray-700 text-white' 
                          : 'text-white hover:bg-blue-700 dark:hover:bg-gray-600'
                      }`}
                    >
                      {item.name}
                    </Link>
                  </motion.div>
                ))}
                
                {currentUser ? (
                  <motion.div
                    variants={menuItemVariants}
                    custom={filteredMenuItems.length}
                  >
                    <button 
                      onClick={logout}
                      className="w-full mt-2 px-3 py-2 rounded-md bg-gradient-to-r from-red-500 to-pink-600 text-white text-left"
                    >
                      {texts.logout || defaultMenuItems.logout}
                    </button>
                  </motion.div>
                ) : (
                  <>
                    <motion.div
                      variants={menuItemVariants}
                      custom={filteredMenuItems.length}
                    >
                      <Link 
                        to="/login"
                        className="block w-full mt-2 px-3 py-2 rounded-md bg-white dark:bg-gray-700 text-blue-600 dark:text-white"
                      >
                        {texts.login || defaultMenuItems.login}
                      </Link>
                    </motion.div>
                    <motion.div
                      variants={menuItemVariants}
                      custom={filteredMenuItems.length + 1}
                    >
                      <Link 
                        to="/register"
                        className="block w-full mt-2 px-3 py-2 rounded-md bg-gradient-to-r from-blue-700 to-indigo-800 dark:from-blue-600 dark:to-indigo-700 text-white"
                      >
                        {texts.register || defaultMenuItems.register}
                      </Link>
                    </motion.div>
                  </>
                )}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.nav>
  );
};

export default Navbar;
