import React, { useState, useEffect } from 'react';
import axios from 'axios';

const History = () => {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  useEffect(() => {
    const fetchHistory = async () => {
      try {
        setLoading(true);
        setError('');
        
        const response = await axios.get('http://localhost:5000/api/news/history');
        setHistory(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch history');
      } finally {
        setLoading(false);
      }
    };
    
    fetchHistory();
  }, []);
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };
  
  return (
    <div className="flex flex-col items-center">
      <div className="w-full">
        <h2 className="text-2xl font-bold mb-6 text-center">Your News Check History</h2>
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}
        
        {loading ? (
          <div className="text-center py-8">Loading...</div>
        ) : history.length === 0 ? (
          <div className="text-center py-8 bg-gray-50 rounded-lg">
            <p className="text-gray-600">You haven't checked any news yet</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white">
              <thead className="bg-gray-100">
                <tr>
                  <th className="py-3 px-4 text-left">Date</th>
                  <th className="py-3 px-4 text-left">Title</th>
                  <th className="py-3 px-4 text-left">Result</th>
                  <th className="py-3 px-4 text-left">Confidence</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {history.map((item) => (
                  <tr key={item.id} className="hover:bg-gray-50">
                    <td className="py-3 px-4 text-sm text-gray-500">{formatDate(item.checked_at)}</td>
                    <td className="py-3 px-4">
                      <div className="font-medium">{item.title}</div>
                      <div className="text-sm text-gray-500 truncate max-w-md">{item.description}</div>
                    </td>
                    <td className="py-3 px-4">
                      <span className={`px-3 py-1 rounded-full text-xs font-medium ${item.result ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                        {item.result ? 'REAL' : 'FAKE'}
                      </span>
                    </td>
                    <td className="py-3 px-4">
                      <div className="w-32 bg-gray-200 rounded-full h-2">
                        <div 
                          className={`h-2 rounded-full ${item.result ? 'bg-green-500' : 'bg-red-500'}`}
                          style={{ width: `${item.confidence_percentage}%` }}
                        ></div>
                      </div>
                      <div className="text-xs mt-1">{item.confidence_percentage}%</div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default History;
