import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import Lottie from 'lottie-react';
import checkAnimation from '../assets/check-animation.json';

const CheckNews = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  
  // Animation refs and state
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });
  
  const formVariants = {
    hidden: { 
      opacity: 0, 
      y: 50 
    },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.8, 
        ease: "easeOut" 
      }
    }
  };
  
  const inputVariants = {
    focus: { 
      scale: 1.02, 
      boxShadow: "0px 5px 20px rgba(0,0,0,0.15)"
    },
    blur: { 
      scale: 1, 
      boxShadow: "0px 0px 0px rgba(0,0,0,0.1)"
    }
  };
  
  const resultVariants = {
    hidden: { 
      opacity: 0, 
      y: 30,
      scale: 0.95 
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: { 
        type: "spring", 
        damping: 20, 
        stiffness: 100 
      }
    },
    exit: { 
      opacity: 0, 
      y: -20, 
      transition: { 
        duration: 0.3 
      } 
    }
  };
  
  const buttonVariants = {
    hover: { 
      scale: 1.05,
      backgroundColor: "#2563EB",
      boxShadow: "0px 5px 15px rgba(37, 99, 235, 0.4)"
    },
    tap: { 
      scale: 0.95 
    },
    disabled: {
      opacity: 0.7,
      cursor: "not-allowed"
    }
  };
  
  const progressVariants = {
    hidden: { width: 0 },
    visible: width => ({
      width: `${width}%`,
      transition: { 
        duration: 0.8, 
        ease: "easeOut" 
      }
    })
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setError('');
      setResult(null);
      setLoading(true);
      
      const response = await axios.post('http://localhost:5000/api/news/check', {
        title,
        description
      });
      
      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to check news');
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="flex flex-col items-center py-10 min-h-screen bg-gradient-to-b from-blue-50 to-white">
      <motion.div 
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-4xl mb-8 text-center"
      >
        <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-2">Detect Fake News</h1>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto">
          Submit news content to analyze its credibility using our advanced AI technology
        </p>
      </motion.div>
      
      <div className="w-full max-w-4xl flex flex-col md:flex-row gap-8 px-4">
        <motion.div 
          ref={ref}
          className="bg-white shadow-lg rounded-2xl px-8 pt-6 pb-8 mb-4 flex-1"
          variants={formVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <h2 className="text-2xl font-bold mb-6 text-center text-blue-800">Check News</h2>
          
          {error && (
            <motion.div 
              className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded mb-4"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.4 }}
            >
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <p className="text-sm">{error}</p>
                </div>
              </div>
            </motion.div>
          )}
          
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="title">
                News Title
              </label>
              <motion.input
                className="shadow-sm appearance-none border border-gray-300 rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
                id="title"
                type="text"
                placeholder="Enter the title of the news article"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                variants={inputVariants}
                initial="blur"
                whileFocus="focus"
                whileTap="focus"
                required
              />
            </div>
            
            <div>
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
                News Description
              </label>
              <motion.textarea
                className="shadow-sm appearance-none border border-gray-300 rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
                id="description"
                placeholder="Enter the content or description of the news article"
                rows="7"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                variants={inputVariants}
                initial="blur"
                whileFocus="focus"
                whileTap="focus"
                required
              />
            </div>
            
            <div className="flex items-center justify-center pt-2">
              <motion.button
                className="bg-blue-600 text-white font-bold py-3 px-6 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 w-full md:w-auto transition-all duration-200"
                type="submit"
                disabled={loading}
                variants={buttonVariants}
                whileHover={!loading ? "hover" : "disabled"}
                whileTap={!loading ? "tap" : "disabled"}
                initial={{ opacity: 0.9 }}
                animate={{ opacity: 1 }}
              >
                <span className="flex items-center justify-center">
                  {loading ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Analyzing...
                    </>
                  ) : (
                    <>
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                      </svg>
                      Analyze News
                    </>
                  )}
                </span>
              </motion.button>
            </div>
          </form>
        </motion.div>
        
        <AnimatePresence>
          {result && (
            <motion.div 
              key="result"
              className="flex-1"
              variants={resultVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <div className={`bg-white shadow-lg rounded-2xl p-6 mb-4 border-t-8 ${result.result ? 'border-green-500' : 'border-red-500'}`}>
                <div className="flex justify-between items-center mb-6">
                  <h3 className="text-2xl font-bold text-gray-800">Analysis Result</h3>
                  <div className={`px-4 py-1 rounded-full text-white font-semibold ${result.result ? 'bg-green-500' : 'bg-red-500'}`}>
                    {result.result ? 'REAL' : 'FAKE'}
                  </div>
                </div>
                
                <div className="mb-6">
                  <div className="flex justify-between mb-2">
                    <span className="font-medium text-gray-700">Confidence Score</span>
                    <span className="font-bold text-blue-700">{result.confidencePercentage}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden">
                    <motion.div 
                      className={`h-4 rounded-full ${result.result ? 'bg-green-500' : 'bg-red-500'}`}
                      custom={result.confidencePercentage}
                      variants={progressVariants}
                      initial="hidden"
                      animate="visible"
                    ></motion.div>
                  </div>
                </div>
                
                <div className="mb-6">
                  <div className="font-medium text-gray-700 mb-2">Analyzed News</div>
                  <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
                    <div className="font-semibold text-gray-800 mb-2">{result.title}</div>
                    <div className="text-gray-600">{result.description}</div>
                  </div>
                </div>
                
                {result.analysis && (
                  <div>
                    <div className="font-medium text-gray-700 mb-2">Detailed Analysis</div>
                    <div className="bg-gray-50 p-4 rounded-lg border border-gray-200 whitespace-pre-line text-sm text-gray-700 max-h-64 overflow-y-auto">
                      {result.analysis}
                    </div>
                  </div>
                )}
                
                <div className="mt-6 flex justify-center">
                  {result.result ? (
                    <div className="w-32">
                      <Lottie animationData={checkAnimation} loop={false} />
                    </div>
                  ) : (
                    <motion.div 
                      initial={{ rotate: 0 }}
                      animate={{ rotate: [0, 5, -5, 0] }}
                      transition={{ 
                        duration: 0.5, 
                        repeat: 1, 
                        repeatType: "reverse" 
                      }}
                      className="w-12 h-12 bg-red-100 rounded-full flex items-center justify-center text-red-500"
                    >
                      <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                      </svg>
                    </motion.div>
                  )}
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
      
      <motion.div 
        className="w-full max-w-4xl px-4 mt-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.5 }}
      >
        <div className="bg-blue-50 border-l-4 border-blue-500 text-blue-700 p-4 rounded-lg shadow-sm">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-blue-500" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2h-1V9a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm">
                Our AI uses advanced natural language processing to analyze news content. While highly accurate, we recommend using this tool as one of many resources for verifying information.
              </p>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default CheckNews;
