import React, { useState, useContext } from 'react';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import Lottie from 'lottie-react';
import checkAnimation from '../assets/check-animation.json';
import { AuthContext } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import Tooltip from '../components/Tooltip';

const CheckNews = () => {
  const [newsTitle, setNewsTitle] = useState('');
  const [newsContent, setNewsContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const { currentUser } = useContext(AuthContext);
  const { theme } = useTheme();
  const { texts } = useLanguage() || {};
  
  // Default text values if translations aren't loaded yet
  const defaultTexts = {
    detectFakeNews: 'Detect Fake News',
    submitNewsPrompt: 'Submit news content to analyze its credibility using our advanced AI technology',
    checkNewsTitle: 'Check News',
    newsTitle: 'News Title',
    newsDescription: 'News Description',
    enterNewsTitle: 'Enter the title of the news article',
    enterNewsDescription: 'Enter the content or description of the news article',
    analyzeNews: 'Analyze News',
    analyzing: 'Analyzing...',
    analysisResult: 'Analysis Result',
    real: 'REAL',
    fake: 'FAKE',
    confidenceScore: 'Confidence Score',
    analyzedNews: 'Analyzed News',
    detailedAnalysis: 'Detailed Analysis',
    aiInfoText: 'Our AI uses advanced natural language processing to analyze news content. While highly accurate, we recommend using this tool as one of many resources for verifying information.'
  };
  
  // Animation refs and state
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });
  
  const formVariants = {
    hidden: { 
      opacity: 0, 
      y: 50 
    },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        type: "spring",
        damping: 20,
        stiffness: 100
      }
    }
  };
  
  const inputVariants = {
    focus: { 
      scale: 1.02, 
      boxShadow: theme === 'dark' 
        ? "0px 5px 20px rgba(0,0,0,0.5)" 
        : "0px 5px 20px rgba(0,0,0,0.15)"
    },
    blur: { 
      scale: 1, 
      boxShadow: "0px 0px 0px rgba(0,0,0,0.1)"
    }
  };
  
  const resultVariants = {
    hidden: { 
      opacity: 0, 
      y: 30,
      scale: 0.95 
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: { 
        type: "spring", 
        damping: 20, 
        stiffness: 100 
      }
    },
    exit: { 
      opacity: 0, 
      y: -20, 
      transition: { 
        duration: 0.3 
      } 
    }
  };
  
  const buttonVariants = {
    hover: { 
      scale: 1.05,
      boxShadow: theme === 'dark'
        ? "0px 5px 15px rgba(29, 78, 216, 0.6)"
        : "0px 5px 15px rgba(37, 99, 235, 0.4)"
    },
    tap: { 
      scale: 0.95 
    },
    disabled: {
      opacity: 0.7,
      cursor: "not-allowed"
    }
  };
  
  const progressVariants = {
    hidden: { width: 0 },
    visible: width => ({
      width: `${width}%`,
      transition: { 
        duration: 0.8, 
        ease: "easeOut" 
      }
    })
  };

  // Staggered container animation
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { type: "spring", stiffness: 100, damping: 15 }
    }
  };
  
  const handleAnalyze = async (e) => {
    e.preventDefault();
    
    try {
      setError(null);
      setLoading(true);
      setResult(null);
      
      const response = await axios.post('/api/check-news', {
        title: newsTitle,
        content: newsContent
      });
      
      setResult(response.data);
      
      // Save to history if user is logged in
      if (currentUser) {
        await axios.post('/api/history', {
          title: newsTitle,
          content: newsContent,
          result: response.data.is_fake ? 'fake' : 'real',
          confidence: response.data.confidence
        });
      }
    } catch (err) {
      setError(
        err.response?.data?.message || 
        'Failed to analyze news. Please try again later.'
      );
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className={`min-h-screen flex flex-col ${
      theme === 'dark' 
        ? 'bg-gradient-to-b from-gray-900 to-gray-800 text-white' 
        : 'bg-gradient-to-b from-blue-50 to-white text-gray-800'
    } transition-colors duration-300`}>
      <motion.div 
        className="container mx-auto px-4 py-10 flex-1 flex flex-col"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <motion.div 
          className="text-center mb-8"
          variants={itemVariants}
        >
          <h1 className={`text-3xl md:text-4xl font-bold mb-3 bg-clip-text text-transparent 
            ${theme === 'dark' 
              ? 'bg-gradient-to-r from-blue-400 to-indigo-500' 
              : 'bg-gradient-to-r from-blue-600 to-indigo-700'
            }`}
          >
            {texts?.detectFakeNews || defaultTexts.detectFakeNews}
          </h1>
          <p className={`max-w-2xl mx-auto ${theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}`}>
            {texts?.submitNewsPrompt || defaultTexts.submitNewsPrompt}
          </p>
        </motion.div>
        
        <div className="flex flex-col lg:flex-row gap-8">
          <motion.div 
            className={`w-full lg:w-1/2 rounded-xl shadow-lg p-6 ${
              theme === 'dark' 
                ? 'bg-gray-800/80 backdrop-blur-sm border border-gray-700' 
                : 'bg-white/80 backdrop-blur-sm border border-gray-100'
            }`}
            variants={itemVariants}
          >
            <h2 className="text-xl font-bold mb-4">{texts?.checkNewsTitle || defaultTexts.checkNewsTitle}</h2>
            
            {error && (
              <motion.div 
                className={`mb-4 p-3 rounded-lg ${
                  theme === 'dark' 
                    ? 'bg-red-900/30 border border-red-800 text-red-300' 
                    : 'bg-red-100 border border-red-300 text-red-700'
                }`}
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
              >
                {error}
              </motion.div>
            )}
            
            <form onSubmit={handleAnalyze}>
              <div className="mb-4">
                <label 
                  className={`block text-sm font-medium mb-2 ${
                    theme === 'dark' ? 'text-gray-300' : 'text-gray-700'
                  }`}
                  htmlFor="newsTitle"
                >
                  {texts?.newsTitle || defaultTexts.newsTitle}
                </label>
                <motion.input
                  type="text"
                  id="newsTitle"
                  value={newsTitle}
                  onChange={(e) => setNewsTitle(e.target.value)}
                  required
                  placeholder={texts?.enterNewsTitle || defaultTexts.enterNewsTitle}
                  className={`w-full px-4 py-2 rounded-lg appearance-none focus:outline-none transition-all 
                    ${theme === 'dark' 
                      ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400 focus:border-blue-500' 
                      : 'bg-gray-50 border-gray-300 text-gray-900 placeholder-gray-500 focus:border-blue-500'
                    } border`}
                  whileFocus="focus"
                  variants={inputVariants}
                />
              </div>
              
              <div className="mb-4">
                <label 
                  className={`block text-sm font-medium mb-2 ${
                    theme === 'dark' ? 'text-gray-300' : 'text-gray-700'
                  }`}
                  htmlFor="newsContent"
                >
                  {texts?.newsDescription || defaultTexts.newsDescription}
                </label>
                <motion.textarea
                  id="newsContent"
                  value={newsContent}
                  onChange={(e) => setNewsContent(e.target.value)}
                  required
                  placeholder={texts?.enterNewsDescription || defaultTexts.enterNewsDescription}
                  className={`w-full px-4 py-2 rounded-lg appearance-none focus:outline-none transition-all 
                    ${theme === 'dark' 
                      ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400 focus:border-blue-500' 
                      : 'bg-gray-50 border-gray-300 text-gray-900 placeholder-gray-500 focus:border-blue-500'
                    } border h-40 resize-none`}
                  whileFocus="focus"
                  variants={inputVariants}
                />
              </div>
              
              <Tooltip text={texts?.tooltipAnalyze || 'Analyze with AI'}>
                <motion.button
                  type="submit"
                  disabled={loading}
                  className={`w-full py-3 px-4 rounded-lg font-medium text-white transition-colors
                    ${loading
                      ? theme === 'dark' 
                        ? 'bg-blue-800 cursor-not-allowed' 
                        : 'bg-blue-400 cursor-not-allowed'
                      : theme === 'dark'
                        ? 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700'
                        : 'bg-gradient-to-r from-blue-500 to-indigo-500 hover:from-blue-600 hover:to-indigo-600'
                    }`}
                  whileHover={!loading ? "hover" : {}}
                  whileTap={!loading ? "tap" : {}}
                  variants={buttonVariants}
                >
                  {loading 
                    ? <span className="flex items-center justify-center">
                        <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                        {texts?.analyzing || defaultTexts.analyzing}
                      </span>
                    : texts?.analyzeNews || defaultTexts.analyzeNews
                  }
                </motion.button>
              </Tooltip>
              
              <p className={`mt-4 text-xs ${theme === 'dark' ? 'text-gray-400' : 'text-gray-600'}`}>
                {texts?.aiInfoText || defaultTexts.aiInfoText}
              </p>
            </form>
          </motion.div>
          
          <AnimatePresence>
            {result && (
              <motion.div 
                className={`w-full lg:w-1/2 rounded-xl shadow-lg overflow-hidden ${
                  theme === 'dark' 
                    ? 'bg-gray-800/80 backdrop-blur-sm border border-gray-700' 
                    : 'bg-white/80 backdrop-blur-sm border border-gray-100'
                }`}
                variants={resultVariants}
                initial="hidden"
                animate="visible"
                exit="hidden"
              >
                <div className={`p-4 text-center ${
                  result.is_fake
                    ? theme === 'dark' 
                      ? 'bg-red-900/60 text-white' 
                      : 'bg-red-100 text-red-800'
                    : theme === 'dark'
                      ? 'bg-green-900/60 text-white'
                      : 'bg-green-100 text-green-800'
                }`}>
                  <h2 className="text-xl font-bold">
                    {texts?.analysisResult || defaultTexts.analysisResult}
                  </h2>
                  <div className="mt-2 text-3xl font-extrabold">
                    {result.is_fake 
                      ? texts?.fake || defaultTexts.fake
                      : texts?.real || defaultTexts.real
                    }
                  </div>
                  <div className="mt-1">
                    <span className="font-medium">{texts?.confidenceScore || defaultTexts.confidenceScore}:</span> {result.confidence}%
                  </div>
                </div>
                
                <div className="p-6">
                  <h3 className="font-semibold mb-2">{texts?.analyzedNews || defaultTexts.analyzedNews}</h3>
                  <div className={`p-3 rounded-lg mb-4 text-sm ${
                    theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'
                  }`}>
                    <div className="font-medium">{newsTitle}</div>
                    <div className={`mt-2 ${theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}`}>{newsContent}</div>
                  </div>
                  
                  <h3 className="font-semibold mb-2">{texts?.detailedAnalysis || defaultTexts.detailedAnalysis}</h3>
                  <div className={`p-3 rounded-lg text-sm ${
                    theme === 'dark' ? 'bg-gray-700 text-gray-300' : 'bg-gray-50 text-gray-600'
                  }`}>
                    {result.analysis}
                  </div>
                  
                  <div className="flex mt-4 gap-3">
                    <Tooltip text={texts?.tooltipShare || 'Share'}>
                      <motion.button
                        className={`p-2 rounded-lg ${
                          theme === 'dark' 
                            ? 'bg-gray-700 hover:bg-gray-600 text-white' 
                            : 'bg-gray-100 hover:bg-gray-200 text-gray-800'
                        }`}
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        onClick={() => {
                          navigator.clipboard.writeText(
                            `Fake News Detector Analysis:\n\n` +
                            `Title: ${newsTitle}\n` +
                            `Result: ${result.is_fake ? 'FAKE' : 'REAL'}\n` +
                            `Confidence: ${result.confidence}%`
                          );
                          alert('Analysis copied to clipboard!');
                        }}
                      >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.368 2.684 3 3 0 00-5.368-2.684z" />
                        </svg>
                      </motion.button>
                    </Tooltip>
                    
                    <Tooltip text={texts?.tooltipCopy || 'Copy'}>
                      <motion.button
                        className={`p-2 rounded-lg ${
                          theme === 'dark' 
                            ? 'bg-gray-700 hover:bg-gray-600 text-white' 
                            : 'bg-gray-100 hover:bg-gray-200 text-gray-800'
                        }`}
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        onClick={() => {
                          navigator.clipboard.writeText(result.analysis);
                          alert('Analysis copied to clipboard!');
                        }}
                      >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 5H6a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2v-1M8 5a2 2 0 002 2h2a2 2 0 002-2M8 5a2 2 0 012-2h2a2 2 0 012 2m0 0h2a2 2 0 012 2v3m2 4H10m0 0l3-3m-3 3l3 3" />
                        </svg>
                      </motion.button>
                    </Tooltip>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.div>
    </div>
  );
};

export default CheckNews;
